import React, { useState } from 'react';

// Icon components (matching the style from HomeDashboard)
const CloseIcon = () => (
  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
  </svg>
);

const BackArrowIcon = () => (
  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
  </svg>
);

const SearchIcon = () => (
  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
  </svg>
);

const ChevronDownIcon = () => (
  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
  </svg>
);

const PlusIcon = () => (
  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
  </svg>
);

const SymptomIcon = () => <span className="text-2xl">🏥</span>;
const SeverityIcon = ({ level }: { level: 1 | 2 | 3 | 4 | 5 }) => {
  const colors = {
    1: 'text-green-500',
    2: 'text-yellow-500', 
    3: 'text-orange-500',
    4: 'text-red-500',
    5: 'text-red-600'
  };
  return <span className={`text-xl ${colors[level]}`}>●</span>;
};

interface LogSymptomModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const LogSymptomModal: React.FC<LogSymptomModalProps> = ({ isOpen, onClose }) => {
  const [selectedSymptom, setSelectedSymptom] = useState('');
  const [severity, setSeverity] = useState<number>(3);
  const [notes, setNotes] = useState('');
  const [currentStep, setCurrentStep] = useState(1);

  const symptoms = [
    'Cramps',
    'Bloating',
    'Fatigue',
    'Mood swings',
    'Headache',
    'Back pain',
    'Breast tenderness',
    'Food cravings',
    'Acne',
    'Other'
  ];

  const handleSubmit = () => {
    // Handle symptom logging logic here
    console.log('Symptom logged:', { selectedSymptom, severity, notes });
    onClose();
    // Reset form
    setSelectedSymptom('');
    setSeverity(3);
    setNotes('');
    setCurrentStep(1);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl max-w-md w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <div className="flex items-center space-x-3">
            <SymptomIcon />
            <h2 className="text-lg font-semibold text-gray-900">Log Symptom</h2>
          </div>
          <button
            onClick={onClose}
            className="p-1 text-gray-400 hover:text-gray-600 transition-colors"
          >
            <CloseIcon />
          </button>
        </div>

        {/* Content */}
        <div className="p-4">
          {/* Step Indicator */}
          <div className="flex items-center justify-center mb-6">
            <div className="flex items-center space-x-2">
              {[1, 2, 3].map((step) => (
                <div key={step} className="flex items-center">
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                    step <= currentStep 
                      ? 'bg-indigo-600 text-white' 
                      : 'bg-gray-200 text-gray-600'
                  }`}>
                    {step}
                  </div>
                  {step < 3 && (
                    <div className={`w-8 h-0.5 mx-2 ${
                      step < currentStep ? 'bg-indigo-600' : 'bg-gray-200'
                    }`} />
                  )}
                </div>
              ))}
            </div>
          </div>

          {/* Step 1: Select Symptom */}
          {currentStep === 1 && (
            <div>
              <h3 className="text-md font-semibold text-gray-900 mb-4">What symptom are you experiencing?</h3>
              <div className="grid grid-cols-2 gap-3">
                {symptoms.map((symptom) => (
                  <button
                    key={symptom}
                    onClick={() => setSelectedSymptom(symptom)}
                    className={`p-3 rounded-lg border-2 text-left transition-colors ${
                      selectedSymptom === symptom
                        ? 'border-indigo-500 bg-indigo-50 text-indigo-700'
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    {symptom}
                  </button>
                ))}
              </div>
              {selectedSymptom && (
                <button
                  onClick={() => setCurrentStep(2)}
                  className="btn-primary w-full mt-4"
                >
                  Continue
                </button>
              )}
            </div>
          )}

          {/* Step 2: Severity Level */}
          {currentStep === 2 && (
            <div>
              <h3 className="text-md font-semibold text-gray-900 mb-4">How severe is it?</h3>
              <div className="space-y-3">
                {[1, 2, 3, 4, 5].map((level) => (
                  <button
                    key={level}
                    onClick={() => setSeverity(level)}
                    className={`w-full p-3 rounded-lg border-2 flex items-center justify-between transition-colors ${
                      severity === level
                        ? 'border-indigo-500 bg-indigo-50'
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    <span className="text-sm">
                      {level === 1 && 'Very Mild'}
                      {level === 2 && 'Mild'}
                      {level === 3 && 'Moderate'}
                      {level === 4 && 'Severe'}
                      {level === 5 && 'Very Severe'}
                    </span>
                    <SeverityIcon level={level as 1 | 2 | 3 | 4 | 5} />
                  </button>
                ))}
              </div>
              <div className="flex space-x-3 mt-4">
                <button
                  onClick={() => setCurrentStep(1)}
                  className="btn-secondary flex-1"
                >
                  Back
                </button>
                <button
                  onClick={() => setCurrentStep(3)}
                  className="btn-primary flex-1"
                >
                  Continue
                </button>
              </div>
            </div>
          )}

          {/* Step 3: Additional Notes */}
          {currentStep === 3 && (
            <div>
              <h3 className="text-md font-semibold text-gray-900 mb-4">Any additional notes?</h3>
              <textarea
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Describe your symptoms, triggers, or any other details..."
                className="w-full p-3 border border-gray-300 rounded-lg resize-none h-24 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
              />
              <div className="flex space-x-3 mt-4">
                <button
                  onClick={() => setCurrentStep(2)}
                  className="btn-secondary flex-1"
                >
                  Back
                </button>
                <button
                  onClick={handleSubmit}
                  className="btn-primary flex-1"
                >
                  Log Symptom
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default LogSymptomModal;
