  onTextSizeChange: (size: 'small' | 'medium' | 'large') => void;
  onHighContrastToggle: () => void;
  onShare: () => void;
  onPrint: () => void;
}

const ModuleSubheader: React.FC<ModuleSubheaderProps> = ({
