import React, { useState } from 'react';

// Icon components
const BackArrowIcon = () => (
  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
  </svg>
);

interface TrackerCardProps {
  icon: string;
  title: string;
  status: string;
  buttonText: string;
  onClick: () => void;
}

const TrackerCard: React.FC<TrackerCardProps> = ({
  icon,
  title,
  status,
  buttonText,
  onClick
}) => (
  <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm hover:shadow-md transition-shadow duration-200">
    <div className="flex items-center justify-between">
      <div className="flex items-center space-x-4">
        <div className="flex-shrink-0">
          <span className="text-3xl" role="img" aria-label={`${title} icon`}>{icon}</span>
        </div>
        <div className="flex-1 min-w-0">
          <h3 className="text-base font-semibold text-gray-900 mb-1">{title}</h3>
          <p className="text-sm text-gray-600">{status}</p>
        </div>
      </div>
      <button
        onClick={onClick}
        className="bg-indigo-600 text-white font-medium px-6 py-3 rounded-lg hover:bg-indigo-700 active:bg-indigo-800 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 focus:ring-offset-white min-h-[44px] min-w-[100px]"
        aria-label={`${buttonText} for ${title}`}
      >
        {buttonText}
      </button>
    </div>
  </div>
);

interface LogAndTrackScreenProps {
  onBack: () => void;
  onNavigateToNutrition?: () => void;
}

const LogAndTrackScreen: React.FC<LogAndTrackScreenProps> = ({ 
  onBack,
  onNavigateToNutrition 
}) => {
  const handleLogSymptom = () => {
    // This would trigger the existing symptom logging flow
    console.log('Navigate to symptom logging flow');
  };

  const handleViewNutritionPlan = () => {
    if (onNavigateToNutrition) {
      onNavigateToNutrition();
    }
  };

  const handleLogMedication = () => {
    // This would navigate to the new medication logging flow (to be built)
    console.log('Navigate to medication logging flow');
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200 px-4 py-4">
        <div className="flex items-center justify-between">
          <button 
            onClick={onBack}
            className="p-2 text-gray-400 hover:text-gray-600 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 rounded-lg min-h-[44px] min-w-[44px] flex items-center justify-center"
            aria-label="Go back"
          >
            <BackArrowIcon />
          </button>
          <div className="flex-1 text-center">
            <h1 className="text-xl font-semibold text-gray-900">Log & Track</h1>
          </div>
          <div className="w-10"></div> {/* Spacer for centering */}
        </div>
      </header>

      {/* Main Content */}
      <main className="px-4 py-8">
        {/* Three Unified Tracker Cards */}
        <div className="space-y-6 max-w-2xl mx-auto">
          <TrackerCard
            icon="ðŸ™‚"
            title="Symptom Status"
            status="No new symptoms logged"
            buttonText="Log Symptom"
            onClick={handleLogSymptom}
          />

          <TrackerCard
            icon="ðŸ½ï¸"
            title="Nutrition Plan"
            status="Today's plan ready"
            buttonText="View Plan"
            onClick={handleViewNutritionPlan}
          />

          <TrackerCard
            icon="ðŸ’Š"
            title="Medication Tracker"
            status="No medication logged today"
            buttonText="Log Medication"
            onClick={handleLogMedication}
          />
        </div>
      </main>
    </div>
  );
};

export default LogAndTrackScreen;
