import React, { useState } from 'react';

// Icon components
const BackArrowIcon = () => (
  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
  </svg>
);

const AlertIcon = ({ type }: { type: 'urgent' | 'warning' | 'info' }) => {
  const icons = {
    urgent: 'üö®',
    warning: '‚ö†Ô∏è',
    info: '‚ÑπÔ∏è'
  };
  return <span className="text-2xl">{icons[type]}</span>;
};

const CheckIcon = () => (
  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
  </svg>
);

const ClockIcon = () => (
  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
  </svg>
);

interface Alert {
  id: string;
  type: 'urgent' | 'warning' | 'info';
  title: string;
  description: string;
  timestamp: string;
  category: 'nutrition' | 'symptom' | 'medication' | 'general';
  isRead: boolean;
  actionRequired?: boolean;
  actionText?: string;
}

interface AlertsScreenProps {
  onBack: () => void;
}

const AlertsScreen: React.FC<AlertsScreenProps> = ({ onBack }) => {
  const [alerts, setAlerts] = useState<Alert[]>([
    {
      id: '1',
      type: 'urgent',
      title: 'Low Iron Levels Detected',
      description: 'Jamie\'s recent blood work shows iron levels below recommended range. Consider increasing iron-rich foods or consult with healthcare provider.',
      timestamp: '2 hours ago',
      category: 'nutrition',
      isRead: false,
      actionRequired: true,
      actionText: 'View Nutrition Plan'
    },
    {
      id: '2',
      type: 'warning',
      title: 'Symptom Pattern Alert',
      description: 'Fatigue symptoms logged 3 times this week. This may indicate a pattern that requires attention.',
      timestamp: '1 day ago',
      category: 'symptom',
      isRead: false,
      actionRequired: true,
      actionText: 'Review Symptom History'
    },
    {
      id: '3',
      type: 'info',
      title: 'Medication Reminder',
      description: 'Time to refill Jamie\'s vitamin D supplement. Current supply will last 5 more days.',
      timestamp: '2 days ago',
      category: 'medication',
      isRead: true,
      actionRequired: false,
      actionText: 'Set Reminder'
    },
    {
      id: '4',
      type: 'info',
      title: 'Growth Milestone',
      description: 'Jamie has grown 2cm this month! This is within normal range for her age.',
      timestamp: '3 days ago',
      category: 'general',
      isRead: true,
      actionRequired: false
    }
  ]);

  const [selectedCategory, setSelectedCategory] = useState<string>('all');

  const handleMarkAsRead = (alertId: string) => {
    setAlerts(alerts.map(alert => 
      alert.id === alertId ? { ...alert, isRead: true } : alert
    ));
  };

  const handleDismiss = (alertId: string) => {
    setAlerts(alerts.filter(alert => alert.id !== alertId));
  };

  const filteredAlerts = selectedCategory === 'all' 
    ? alerts 
    : alerts.filter(alert => alert.category === selectedCategory);

  const unreadCount = alerts.filter(alert => !alert.isRead).length;
  const urgentCount = alerts.filter(alert => alert.type === 'urgent' && !alert.isRead).length;

  const getCategoryColor = (type: string) => {
    switch (type) {
      case 'urgent': return 'bg-red-100 text-red-800 border-red-200';
      case 'warning': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'info': return 'bg-blue-100 text-blue-800 border-blue-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const categories = [
    { key: 'all', label: 'All', count: alerts.length },
    { key: 'urgent', label: 'Urgent', count: urgentCount },
    { key: 'nutrition', label: 'Nutrition', count: alerts.filter(a => a.category === 'nutrition').length },
    { key: 'symptom', label: 'Symptoms', count: alerts.filter(a => a.category === 'symptom').length },
    { key: 'medication', label: 'Medication', count: alerts.filter(a => a.category === 'medication').length }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200 px-4 py-3">
        <div className="flex items-center justify-between">
          <button 
            onClick={onBack}
            className="p-2 text-gray-400 hover:text-gray-600 transition-colors"
          >
            <BackArrowIcon />
          </button>
          <div className="flex-1 text-center">
            <h1 className="text-lg font-semibold text-gray-900">Alerts</h1>
            <p className="text-sm text-gray-600">Stay informed about Jamie's safety and care.</p>
          </div>
          <div className="w-10"></div> {/* Spacer for centering */}
        </div>
      </header>

      {/* Main Content */}
      <main className="px-4 py-6">
        {/* Summary Stats */}
        {unreadCount > 0 && (
          <div className="mb-6 p-4 bg-gradient-to-r from-red-50 to-orange-50 rounded-xl border border-red-200">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-red-900">
                  {unreadCount} unread alert{unreadCount !== 1 ? 's' : ''}
                </p>
                {urgentCount > 0 && (
                  <p className="text-xs text-red-700 mt-1">
                    {urgentCount} urgent alert{urgentCount !== 1 ? 's' : ''} require{urgentCount === 1 ? 's' : ''} attention
                  </p>
                )}
              </div>
              <button 
                onClick={() => setAlerts(alerts.map(alert => ({ ...alert, isRead: true })))}
                className="text-sm text-red-600 hover:text-red-700 font-medium"
              >
                Mark all read
              </button>
            </div>
          </div>
        )}

        {/* Category Filters */}
        <div className="mb-6">
          <div className="flex space-x-2 overflow-x-auto pb-2">
            {categories.map(category => (
              <button
                key={category.key}
                onClick={() => setSelectedCategory(category.key)}
                className={`flex-shrink-0 px-3 py-2 rounded-full text-sm font-medium transition-colors ${
                  selectedCategory === category.key
                    ? 'bg-indigo-600 text-white'
                    : 'bg-white text-gray-600 border border-gray-200 hover:bg-gray-50'
                }`}
              >
                {category.label}
                {category.count > 0 && (
                  <span className={`ml-1 px-1.5 py-0.5 rounded-full text-xs ${
                    selectedCategory === category.key
                      ? 'bg-indigo-500 text-white'
                      : 'bg-gray-200 text-gray-700'
                  }`}>
                    {category.count}
                  </span>
                )}
              </button>
            ))}
          </div>
        </div>

        {/* Alerts List */}
        <div className="space-y-4">
          {filteredAlerts.length === 0 ? (
            <div className="text-center py-12">
              <div className="text-4xl mb-4">üéâ</div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">All clear!</h3>
              <p className="text-gray-600">No alerts to show for this category.</p>
            </div>
          ) : (
            filteredAlerts.map(alert => (
              <div 
                key={alert.id}
                className={`bg-white rounded-xl p-4 border transition-all ${
                  alert.isRead 
                    ? 'border-gray-200 opacity-75' 
                    : 'border-l-4 border-l-red-500 shadow-sm'
                }`}
              >
                <div className="flex items-start space-x-3">
                  <div className="flex-shrink-0">
                    <AlertIcon type={alert.type} />
                  </div>
                  
                  <div className="flex-1 min-w-0">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <h3 className={`text-sm font-semibold ${
                          alert.isRead ? 'text-gray-600' : 'text-gray-900'
                        }`}>
                          {alert.title}
                        </h3>
                        <p className="text-sm text-gray-600 mt-1">
                          {alert.description}
                        </p>
                        
                        <div className="flex items-center mt-3 space-x-4">
                          <div className="flex items-center text-xs text-gray-500">
                            <ClockIcon />
                            <span className="ml-1">{alert.timestamp}</span>
                          </div>
                          
                          <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium border ${getCategoryColor(alert.type)}`}>
                            {alert.category.charAt(0).toUpperCase() + alert.category.slice(1)}
                          </span>
                        </div>
                      </div>
                      
                      {!alert.isRead && (
                        <button
                          onClick={() => handleMarkAsRead(alert.id)}
                          className="ml-2 p-1 text-gray-400 hover:text-green-600 transition-colors"
                          title="Mark as read"
                        >
                          <CheckIcon />
                        </button>
                      )}
                    </div>
                    
                    {/* Action Buttons */}
                    <div className="flex items-center space-x-3 mt-4">
                      {alert.actionText && (
                        <button className="text-sm text-indigo-600 hover:text-indigo-700 font-medium">
                          {alert.actionText}
                        </button>
                      )}
                      
                      <button
                        onClick={() => handleDismiss(alert.id)}
                        className="text-sm text-gray-500 hover:text-gray-700"
                      >
                        Dismiss
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </main>
    </div>
  );
};

export default AlertsScreen;
