import React, { useState } from 'react';

interface GrowthDevelopmentScreenProps {
  onBack: () => void;
}

interface DomainData {
  id: string;
  name: string;
  icon: string;
  observedSkills: number;
  suggestedSkills: number;
  lastSkillName?: string;
  lastSkillDate?: string;
}

interface SkillData {
  id: string;
  name: string;
  domain: string;
  ageRange: string;
  description: string;
}

interface AddSkillForm {
  step: number;
  selectedDomain: string;
  selectedSkill: string;
  status: 'observed' | 'emerging' | 'not-yet' | 'not-applicable';
  dateObserved: string;
  supports: string[];
  context: string[];
  notes: string;
}

const GrowthDevelopmentScreen: React.FC<GrowthDevelopmentScreenProps> = ({ onBack }) => {
  const [showQuickAddSheet, setShowQuickAddSheet] = useState(false);
  const [showReportModal, setShowReportModal] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');

  // Filter states
  const [useDevelopmentalLevel, setUseDevelopmentalLevel] = useState(false);
  const [chronologicalAge, setChronologicalAge] = useState('24 months'); // Prefilled from Profile
  const [developmentalLevel, setDevelopmentalLevel] = useState('Toddler (12–36m)');
  const [timeScope, setTimeScope] = useState('30 days');

  // Add Skill form state
  const [addSkillForm, setAddSkillForm] = useState<AddSkillForm>({
    step: 1,
    selectedDomain: '',
    selectedSkill: '',
    status: 'observed',
    dateObserved: new Date().toISOString().split('T')[0],
    supports: [],
    context: [],
    notes: ''
  });

  const ageOptions = [
    '6 months', '12 months', '18 months', '24 months', '30 months', '36 months',
    '4 years', '5 years', '6 years', '7 years', '8 years', '9 years', '10 years'
  ];

  const developmentalLevels = [
    'Early (0–12m)',
    'Toddler (12–36m)', 
    'Preschool (3–5y)',
    'School-age (5+)'
  ];

  const timeScopeOptions = [
    'This week',
    '30 days',
    '90 days', 
    'Custom'
  ];

  // Domain data - this would come from API in real implementation
  const domains: DomainData[] = [
    {
      id: 'motor',
      name: 'Motor',
      icon: '🏃',
      observedSkills: 8,
      suggestedSkills: 12,
      lastSkillName: 'Walks independently',
      lastSkillDate: '2024-01-15'
    },
    {
      id: 'communication',
      name: 'Communication',
      icon: '💬',
      observedSkills: 5,
      suggestedSkills: 10,
      lastSkillName: 'Says "mama" and "dada"',
      lastSkillDate: '2024-01-10'
    },
    {
      id: 'social-emotional',
      name: 'Social-Emotional',
      icon: '😊',
      observedSkills: 6,
      suggestedSkills: 8,
      lastSkillName: 'Shows affection',
      lastSkillDate: '2024-01-12'
    },
    {
      id: 'cognitive',
      name: 'Cognitive/Play',
      icon: '🧩',
      observedSkills: 4,
      suggestedSkills: 9,
      lastSkillName: 'Stacks 2 blocks',
      lastSkillDate: '2024-01-08'
    },
    {
      id: 'adaptive',
      name: 'Adaptive/Self-Help',
      icon: '👕',
      observedSkills: 3,
      suggestedSkills: 7,
      lastSkillName: 'Feeds self with fingers',
      lastSkillDate: '2024-01-05'
    }
  ];

  // Sample skills data - this would come from API in real implementation
  const allSkills: SkillData[] = [
    // Motor skills
    { id: 'm1', name: 'Walks independently', domain: 'motor', ageRange: '12-18 months', description: 'Takes steps without support' },
    { id: 'm2', name: 'Runs with coordination', domain: 'motor', ageRange: '18-24 months', description: 'Runs with good balance and coordination' },
    { id: 'm3', name: 'Climbs stairs with help', domain: 'motor', ageRange: '18-24 months', description: 'Climbs stairs while holding railing or hand' },
    { id: 'm4', name: 'Throws ball overhand', domain: 'motor', ageRange: '18-24 months', description: 'Throws ball with overhand motion' },
    { id: 'm5', name: 'Kicks ball forward', domain: 'motor', ageRange: '18-24 months', description: 'Kicks ball in forward direction' },
    { id: 'm6', name: 'Stands on one foot', domain: 'motor', ageRange: '24-30 months', description: 'Balances on one foot briefly' },
    { id: 'm7', name: 'Jumps with both feet', domain: 'motor', ageRange: '24-30 months', description: 'Jumps off ground with both feet' },
    { id: 'm8', name: 'Pedals tricycle', domain: 'motor', ageRange: '30-36 months', description: 'Pedals tricycle independently' },
    { id: 'm9', name: 'Catches large ball', domain: 'motor', ageRange: '30-36 months', description: 'Catches large ball with arms' },
    { id: 'm10', name: 'Hops on one foot', domain: 'motor', ageRange: '36-48 months', description: 'Hops on one foot multiple times' },

    // Communication skills
    { id: 'c1', name: 'Says "mama" and "dada"', domain: 'communication', ageRange: '9-12 months', description: 'Uses mama and dada specifically' },
    { id: 'c2', name: 'Points to objects', domain: 'communication', ageRange: '12-15 months', description: 'Points to objects of interest' },
    { id: 'c3', name: 'Follows simple commands', domain: 'communication', ageRange: '12-15 months', description: 'Follows one-step commands' },
    { id: 'c4', name: 'Says 10-20 words', domain: 'communication', ageRange: '15-18 months', description: 'Uses 10-20 recognizable words' },
    { id: 'c5', name: 'Combines 2 words', domain: 'communication', ageRange: '18-24 months', description: 'Combines two words together' },
    { id: 'c6', name: 'Names body parts', domain: 'communication', ageRange: '18-24 months', description: 'Names 3-5 body parts' },
    { id: 'c7', name: 'Uses 50+ words', domain: 'communication', ageRange: '24-30 months', description: 'Uses 50 or more words' },
    { id: 'c8', name: 'Asks questions', domain: 'communication', ageRange: '24-30 months', description: 'Asks "what" and "where" questions' },
    { id: 'c9', name: 'Uses 3-word sentences', domain: 'communication', ageRange: '30-36 months', description: 'Uses 3-word sentences' },
    { id: 'c10', name: 'Tells simple stories', domain: 'communication', ageRange: '36-48 months', description: 'Tells simple stories or events' },

    // Social-Emotional skills
    { id: 's1', name: 'Shows affection', domain: 'social-emotional', ageRange: '12-18 months', description: 'Shows affection to familiar people' },
    { id: 's2', name: 'Plays alongside others', domain: 'social-emotional', ageRange: '18-24 months', description: 'Plays near other children' },
    { id: 's3', name: 'Shows empathy', domain: 'social-emotional', ageRange: '24-30 months', description: 'Shows concern for others' },
    { id: 's4', name: 'Takes turns', domain: 'social-emotional', ageRange: '24-30 months', description: 'Takes turns in simple games' },
    { id: 's5', name: 'Shares toys', domain: 'social-emotional', ageRange: '30-36 months', description: 'Shares toys with prompting' },
    { id: 's6', name: 'Plays cooperatively', domain: 'social-emotional', ageRange: '36-48 months', description: 'Plays cooperatively with others' },

    // Cognitive/Play skills
    { id: 'cp1', name: 'Stacks 2 blocks', domain: 'cognitive', ageRange: '15-18 months', description: 'Stacks 2 blocks on top of each other' },
    { id: 'cp2', name: 'Imitates actions', domain: 'cognitive', ageRange: '18-24 months', description: 'Imitates adult actions' },
    { id: 'cp3', name: 'Matches shapes', domain: 'cognitive', ageRange: '18-24 months', description: 'Matches simple shapes' },
    { id: 'cp4', name: 'Pretend play', domain: 'cognitive', ageRange: '24-30 months', description: 'Engages in pretend play' },
    { id: 'cp5', name: 'Counts to 3', domain: 'cognitive', ageRange: '24-30 months', description: 'Counts objects to 3' },
    { id: 'cp6', name: 'Names colors', domain: 'cognitive', ageRange: '30-36 months', description: 'Names 3-4 colors' },
    { id: 'cp7', name: 'Completes puzzles', domain: 'cognitive', ageRange: '30-36 months', description: 'Completes 3-4 piece puzzles' },

    // Adaptive/Self-Help skills
    { id: 'a1', name: 'Feeds self with fingers', domain: 'adaptive', ageRange: '12-15 months', description: 'Feeds self using fingers' },
    { id: 'a2', name: 'Drinks from cup', domain: 'adaptive', ageRange: '12-15 months', description: 'Drinks from cup with help' },
    { id: 'a3', name: 'Removes shoes', domain: 'adaptive', ageRange: '18-24 months', description: 'Removes shoes independently' },
    { id: 'a4', name: 'Uses spoon', domain: 'adaptive', ageRange: '18-24 months', description: 'Uses spoon to feed self' },
    { id: 'a5', name: 'Washes hands', domain: 'adaptive', ageRange: '24-30 months', description: 'Washes hands with help' },
    { id: 'a6', name: 'Puts on simple clothes', domain: 'adaptive', ageRange: '30-36 months', description: 'Puts on simple clothing items' },
    { id: 'a7', name: 'Uses toilet', domain: 'adaptive', ageRange: '30-36 months', description: 'Uses toilet with help' }
  ];

  const supportOptions = [
    'Hand-over-hand',
    'Adaptive utensil',
    'Prompts',
    'AAC',
    'Brace/orthotics'
  ];

  const contextOptions = [
    'Home',
    'School',
    'Therapy',
    'Community'
  ];

  const handleOpenDomain = (domainId: string) => {
    console.log('Opening domain:', domainId);
    // This will route to domain detail in Phase 5B
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric' 
    });
  };

  const calculateProgress = (observed: number, suggested: number) => {
    return Math.min((observed / suggested) * 100, 100);
  };

  // Add Skill form handlers
  const handleNextStep = () => {
    if (addSkillForm.step < 6) {
      setAddSkillForm(prev => ({ ...prev, step: prev.step + 1 }));
    }
  };

  const handlePrevStep = () => {
    if (addSkillForm.step > 1) {
      setAddSkillForm(prev => ({ ...prev, step: prev.step - 1 }));
    }
  };

  const handleSave = (saveAndAddAnother = false) => {
    // Validate required fields
    if (!addSkillForm.selectedDomain || !addSkillForm.selectedSkill) {
      setToastMessage('Please complete all required fields');
      setShowToast(true);
      return;
    }

    // Save skill logic would go here
    console.log('Saving skill:', addSkillForm);

    setToastMessage('Skill saved. View it in Journal or the domain page.');
    setShowToast(true);

    if (!saveAndAddAnother) {
      setShowQuickAddSheet(false);
      // Reset form
      setAddSkillForm({
        step: 1,
        selectedDomain: '',
        selectedSkill: '',
        status: 'observed',
        dateObserved: new Date().toISOString().split('T')[0],
        supports: [],
        context: [],
        notes: ''
      });
    } else {
      // Reset form but keep domain selection
      setAddSkillForm(prev => ({
        step: 2,
        selectedDomain: prev.selectedDomain,
        selectedSkill: '',
        status: 'observed',
        dateObserved: new Date().toISOString().split('T')[0],
        supports: [],
        context: [],
        notes: ''
      }));
    }
  };

  const handleCancel = () => {
    setShowQuickAddSheet(false);
    // Reset form
    setAddSkillForm({
      step: 1,
      selectedDomain: '',
      selectedSkill: '',
      status: 'observed',
      dateObserved: new Date().toISOString().split('T')[0],
      supports: [],
      context: [],
      notes: ''
    });
  };

  // Filter skills by domain and age
  const getFilteredSkills = () => {
    const domainSkills = allSkills.filter(skill => skill.domain === addSkillForm.selectedDomain);
    // In a real implementation, this would filter by age appropriateness
    return domainSkills;
  };

  const filteredSkills = getFilteredSkills();
  const suggestedSkills = filteredSkills.slice(0, 10);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 px-4 py-4">
        <div className="max-w-7xl mx-auto">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <button
                onClick={onBack}
                className="p-2 text-gray-400 hover:text-gray-600 transition-colors rounded-lg hover:bg-gray-100"
              >
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>
              <div>
                <h1 className="text-xl font-semibold text-gray-900">Growth & Milestones</h1>
                <p className="text-sm text-gray-600">Track observed skills across domains at your child's pace.</p>
              </div>
            </div>

            <div className="flex items-center space-x-3">
              {/* Info Icon */}
              <div className="relative group">
                <button className="p-2 text-gray-400 hover:text-gray-600 transition-colors rounded-lg hover:bg-gray-100">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                </button>
                <div className="absolute bottom-full right-0 mb-2 px-2 py-1 bg-gray-900 text-white text-xs rounded opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-10">
                  Not a diagnostic tool
                  <div className="absolute top-full right-2 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-900"></div>
                </div>
              </div>

              {/* Secondary Action */}
              <button
                onClick={() => setShowReportModal(true)}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
              >
                Generate Milestone Report
              </button>

              {/* Primary Action */}
              <button
                onClick={() => setShowQuickAddSheet(true)}
                className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
              >
                Add Skill
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Filter Bar */}
      <div className="bg-white border-b border-gray-200 px-4 py-3">
        <div className="max-w-7xl mx-auto">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between space-y-3 lg:space-y-0 lg:space-x-6">
            {/* Age Selector */}
            <div className="flex flex-col sm:flex-row sm:items-center space-y-2 sm:space-y-0 sm:space-x-4">
              <label className="text-sm font-medium text-gray-700">Age:</label>
              <select
                value={chronologicalAge}
                onChange={(e) => setChronologicalAge(e.target.value)}
                disabled={useDevelopmentalLevel}
                className="px-3 py-1.5 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 disabled:bg-gray-100 disabled:text-gray-500"
              >
                {ageOptions.map(age => (
                  <option key={age} value={age}>{age}</option>
                ))}
              </select>
            </div>

            {/* Developmental Level Toggle */}
            <div className="flex flex-col sm:flex-row sm:items-center space-y-2 sm:space-y-0 sm:space-x-4">
              <label className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={useDevelopmentalLevel}
                  onChange={(e) => setUseDevelopmentalLevel(e.target.checked)}
                  className="w-4 h-4 text-indigo-600 border-gray-300 rounded focus:ring-indigo-500"
                />
                <span className="text-sm font-medium text-gray-700">Use developmental level instead</span>
              </label>

              {useDevelopmentalLevel && (
                <select
                  value={developmentalLevel}
                  onChange={(e) => setDevelopmentalLevel(e.target.value)}
                  className="px-3 py-1.5 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                >
                  {developmentalLevels.map(level => (
                    <option key={level} value={level}>{level}</option>
                  ))}
                </select>
              )}
            </div>

            {/* Time Scope */}
            <div className="flex flex-col sm:flex-row sm:items-center space-y-2 sm:space-y-0 sm:space-x-4">
              <label className="text-sm font-medium text-gray-700">Time scope:</label>
              <select
                value={timeScope}
                onChange={(e) => setTimeScope(e.target.value)}
                className="px-3 py-1.5 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
              >
                {timeScopeOptions.map(scope => (
                  <option key={scope} value={scope}>{scope}</option>
                ))}
              </select>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 py-6">
        {/* Domain Tiles Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
          {domains.map((domain) => {
            const progress = calculateProgress(domain.observedSkills, domain.suggestedSkills);
            return (
              <div
                key={domain.id}
                className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow cursor-pointer"
                onClick={() => handleOpenDomain(domain.id)}
              >
                {/* Domain Header */}
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center space-x-3">
                    <span className="text-2xl">{domain.icon}</span>
                    <h3 className="text-lg font-semibold text-gray-900">{domain.name}</h3>
                  </div>
                </div>

                {/* Progress Ring */}
                <div className="flex items-center justify-center mb-4">
                  <div className="relative w-20 h-20">
                    <svg className="w-20 h-20 transform -rotate-90" viewBox="0 0 36 36">
                      {/* Background circle */}
                      <path
                        d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                        fill="none"
                        stroke="#e5e7eb"
                        strokeWidth="2"
                      />
                      {/* Progress circle */}
                      <path
                        d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                        fill="none"
                        stroke="#3b82f6"
                        strokeWidth="2"
                        strokeDasharray={`${progress}, 100`}
                        strokeLinecap="round"
                      />
                    </svg>
                    <div className="absolute inset-0 flex items-center justify-center">
                      <span className="text-sm font-semibold text-gray-900">{Math.round(progress)}%</span>
                    </div>
                  </div>
                </div>

                {/* Progress Text */}
                <p className="text-sm text-gray-600 text-center mb-3">
                  Observed {domain.observedSkills} of {domain.suggestedSkills} suggested skills
                </p>

                {/* Last Skill */}
                <p className="text-xs text-gray-500 text-center mb-4">
                  {domain.lastSkillName ? (
                    <>Last new skill: {domain.lastSkillName} on {formatDate(domain.lastSkillDate!)}</>
                  ) : (
                    'No skills yet'
                  )}
                </p>

                {/* CTA Button */}
                <button className="w-full px-4 py-2 text-sm font-medium text-indigo-600 bg-indigo-50 rounded-lg hover:bg-indigo-100 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
                  Open
                </button>
              </div>
            );
          })}
        </div>
      </div>

      {/* Add Skill Quick Sheet */}
      {showQuickAddSheet && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-end justify-center z-50">
          <div className="bg-white rounded-t-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              {/* Header */}
              <div className="flex items-center justify-between mb-6">
                <div>
                  <h3 className="text-lg font-semibold text-gray-900">Add Skill</h3>
                  <p className="text-sm text-gray-600">Step {addSkillForm.step} of 6</p>
                </div>
                <button
                  onClick={handleCancel}
                  className="p-2 text-gray-400 hover:text-gray-600 transition-colors rounded-lg hover:bg-gray-100"
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>

              {/* Progress Bar */}
              <div className="w-full bg-gray-200 rounded-full h-2 mb-6">
                <div 
                  className="bg-indigo-600 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${(addSkillForm.step / 6) * 100}%` }}
                ></div>
              </div>

              {/* Step Content */}
              <div className="space-y-6">
                {/* Step 1: Choose Domain */}
                {addSkillForm.step === 1 && (
                  <div>
                    <h4 className="text-md font-medium text-gray-900 mb-4">Choose Domain *</h4>
                    <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                      {domains.map((domain) => (
                        <button
                          key={domain.id}
                          onClick={() => setAddSkillForm(prev => ({ ...prev, selectedDomain: domain.id }))}
                          className={`p-4 rounded-lg border-2 transition-colors ${
                            addSkillForm.selectedDomain === domain.id
                              ? 'border-indigo-500 bg-indigo-50'
                              : 'border-gray-200 hover:border-gray-300'
                          }`}
                        >
                          <div className="text-center">
                            <span className="text-2xl block mb-2">{domain.icon}</span>
                            <span className="text-sm font-medium text-gray-900">{domain.name}</span>
                          </div>
                        </button>
                      ))}
                    </div>
                  </div>
                )}

                {/* Step 2: Select Skill */}
                {addSkillForm.step === 2 && (
                  <div>
                    <h4 className="text-md font-medium text-gray-900 mb-4">Select Skill *</h4>
                    <div className="space-y-4">
                      <div className="text-sm text-gray-600 mb-4">
                        Top 10 suggested skills for {chronologicalAge}:
                      </div>

                      <div className="space-y-2 max-h-60 overflow-y-auto">
                        {suggestedSkills.map((skill) => (
                          <button
                            key={skill.id}
                            onClick={() => setAddSkillForm(prev => ({ ...prev, selectedSkill: skill.id }))}
                            className={`w-full p-3 rounded-lg border text-left transition-colors ${
                              addSkillForm.selectedSkill === skill.id
                                ? 'border-indigo-500 bg-indigo-50'
                                : 'border-gray-200 hover:border-gray-300'
                            }`}
                          >
                            <div className="font-medium text-gray-900">{skill.name}</div>
                            <div className="text-sm text-gray-600">{skill.description}</div>
                            <div className="text-xs text-gray-500 mt-1">Age: {skill.ageRange}</div>
                          </button>
                        ))}
                      </div>

                      {filteredSkills.length > 10 && (
                        <button className="text-indigo-600 text-sm font-medium hover:text-indigo-700">
                          Show all skills in {domains.find(d => d.id === addSkillForm.selectedDomain)?.name} domain
                        </button>
                      )}
                    </div>
                  </div>
                )}

                {/* Step 3: Mark Status */}
                {addSkillForm.step === 3 && (
                  <div>
                    <h4 className="text-md font-medium text-gray-900 mb-4">Mark Status *</h4>
                    <div className="space-y-3">
                      {[
                        { value: 'observed', label: 'Observed', description: 'Child demonstrates this skill consistently' },
                        { value: 'emerging', label: 'Emerging', description: 'Child shows some signs of this skill' },
                        { value: 'not-yet', label: 'Not yet', description: 'Child has not shown this skill yet' },
                        { value: 'not-applicable', label: 'Not applicable', description: 'This skill is not relevant for this child' }
                      ].map((status) => (
                        <label key={status.value} className="flex items-start space-x-3 cursor-pointer">
                          <input
                            type="radio"
                            name="status"
                            value={status.value}
                            checked={addSkillForm.status === status.value}
                            onChange={(e) => setAddSkillForm(prev => ({ ...prev, status: e.target.value as any }))}
                            className="mt-1 w-4 h-4 text-indigo-600 border-gray-300 focus:ring-indigo-500"
                          />
                          <div>
                            <div className="font-medium text-gray-900">{status.label}</div>
                            <div className="text-sm text-gray-600">{status.description}</div>
                          </div>
                        </label>
                      ))}
                    </div>
                  </div>
                )}

                {/* Step 4: Date Observed */}
                {addSkillForm.step === 4 && (
                  <div>
                    <h4 className="text-md font-medium text-gray-900 mb-4">Date Observed *</h4>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        When did you observe this skill?
                      </label>
                      <input
                        type="date"
                        value={addSkillForm.dateObserved}
                        onChange={(e) => setAddSkillForm(prev => ({ ...prev, dateObserved: e.target.value }))}
                        max={new Date().toISOString().split('T')[0]}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                      />
                    </div>
                  </div>
                )}

                {/* Step 5: Supports Used */}
                {addSkillForm.step === 5 && (
                  <div>
                    <h4 className="text-md font-medium text-gray-900 mb-4">Supports Used (Optional)</h4>
                    <p className="text-sm text-gray-600 mb-4">What supports helped the child demonstrate this skill?</p>
                    <div className="flex flex-wrap gap-2">
                      {supportOptions.map((support) => (
                        <button
                          key={support}
                          onClick={() => {
                            const newSupports = addSkillForm.supports.includes(support)
                              ? addSkillForm.supports.filter(s => s !== support)
                              : [...addSkillForm.supports, support];
                            setAddSkillForm(prev => ({ ...prev, supports: newSupports }));
                          }}
                          className={`px-3 py-2 rounded-full text-sm font-medium transition-colors ${
                            addSkillForm.supports.includes(support)
                              ? 'bg-indigo-100 text-indigo-700 border border-indigo-200'
                              : 'bg-gray-100 text-gray-700 border border-gray-200 hover:bg-gray-200'
                          }`}
                        >
                          {support}
                        </button>
                      ))}
                    </div>
                  </div>
                )}

                {/* Step 6: Context and Notes */}
                {addSkillForm.step === 6 && (
                  <div className="space-y-6">
                    <div>
                      <h4 className="text-md font-medium text-gray-900 mb-4">Context (Optional)</h4>
                      <p className="text-sm text-gray-600 mb-4">Where did you observe this skill?</p>
                      <div className="flex flex-wrap gap-2">
                        {contextOptions.map((context) => (
                          <button
                            key={context}
                            onClick={() => {
                              const newContext = addSkillForm.context.includes(context)
                                ? addSkillForm.context.filter(c => c !== context)
                                : [...addSkillForm.context, context];
                              setAddSkillForm(prev => ({ ...prev, context: newContext }));
                            }}
                            className={`px-3 py-2 rounded-full text-sm font-medium transition-colors ${
                              addSkillForm.context.includes(context)
                                ? 'bg-indigo-100 text-indigo-700 border border-indigo-200'
                                : 'bg-gray-100 text-gray-700 border border-gray-200 hover:bg-gray-200'
                            }`}
                          >
                            {context}
                          </button>
                        ))}
                      </div>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Notes (Optional)
                      </label>
                      <textarea
                        value={addSkillForm.notes}
                        onChange={(e) => setAddSkillForm(prev => ({ ...prev, notes: e.target.value }))}
                        placeholder="Add any additional notes about this skill observation..."
                        maxLength={200}
                        rows={3}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 resize-none"
                      />
                      <div className="text-xs text-gray-500 mt-1 text-right">
                        {addSkillForm.notes.length}/200
                      </div>
                    </div>
                  </div>
                )}
              </div>

              {/* Navigation Buttons */}
              <div className="flex justify-between mt-8">
                <button
                  onClick={handlePrevStep}
                  disabled={addSkillForm.step === 1}
                  className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Previous
                </button>

                <div className="flex space-x-3">
                  {addSkillForm.step === 6 ? (
                    <>
                      <button
                        onClick={() => handleSave(true)}
                        className="px-4 py-2 text-sm font-medium text-indigo-600 bg-indigo-50 border border-indigo-200 rounded-lg hover:bg-indigo-100 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                      >
                        Save & Add Another
                      </button>
                      <button
                        onClick={() => handleSave(false)}
                        className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                      >
                        Save
                      </button>
                    </>
                  ) : (
                    <button
                      onClick={handleNextStep}
                      disabled={
                        (addSkillForm.step === 1 && !addSkillForm.selectedDomain) ||
                        (addSkillForm.step === 2 && !addSkillForm.selectedSkill)
                      }
                      className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      Next
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Report Modal */}
      {showReportModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-md w-full">
            <div className="p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Generate Milestone Report</h3>
              <p className="text-sm text-gray-600 mb-6">
                This feature will be implemented in Phase 5C.
              </p>

              <div className="flex justify-end space-x-3">
                <button
                  onClick={() => setShowReportModal(false)}
                  className="px-4 py-2 bg-indigo-600 text-white rounded-lg font-medium hover:bg-indigo-700 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                >
                  Got it
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Toast */}
      {showToast && (
        <div className="fixed top-4 right-4 z-50 max-w-sm w-full">
          <div className="bg-green-50 border border-green-200 text-green-800 rounded-lg p-4 shadow-lg">
            <div className="flex items-start">
              <div className="flex-shrink-0">
                <svg className="w-5 h-5 text-green-400" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3 flex-1">
                <p className="text-sm font-medium">{toastMessage}</p>
              </div>
              <div className="ml-4 flex-shrink-0">
                <button
                  onClick={() => setShowToast(false)}
                  className="inline-flex text-green-400 hover:text-green-600 focus:outline-none focus:text-green-600"
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default GrowthDevelopmentScreen;
