            <div className="space-y-3">
              <label className="flex items-center">
                <input
                  type="checkbox"
                  checked={typeSpecificFields.straining || false}
                  onChange={(e) => setTypeSpecificFields({...typeSpecificFields, straining: e.target.checked})}
