import React, { useState } from 'react';

// Icon components
const BackArrowIcon = () => (
  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
  </svg>
);

const CheckIcon = () => (
  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
  </svg>
);

const ClockIcon = () => (
  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
  </svg>
);

const ChevronDownIcon = ({ className }: { className?: string }) => (
  <svg className={`w-5 h-5 ${className || ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
  </svg>
);

interface TrackerCardProps {
  icon: string;
  title: string;
  status: string;
  buttonText: string;
  onClick: () => void;
}

const TrackerCard: React.FC<TrackerCardProps> = ({
  icon,
  title,
  status,
  buttonText,
  onClick
}) => (
  <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm hover:shadow-md transition-shadow duration-200">
    <div className="flex items-center justify-between">
      <div className="flex items-center space-x-4">
        <div className="flex-shrink-0">
          <span className="text-3xl" role="img" aria-label={`${title} icon`}>{icon}</span>
        </div>
        <div className="flex-1 min-w-0">
          <h3 className="text-base font-semibold text-gray-900 mb-1">{title}</h3>
          <p className="text-sm text-gray-600">{status}</p>
        </div>
      </div>
      <button
        onClick={onClick}
        className="bg-indigo-600 text-white font-medium px-6 py-3 rounded-lg hover:bg-indigo-700 active:bg-indigo-800 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 focus:ring-offset-white min-h-[44px] min-w-[100px]"
        aria-label={`${buttonText} for ${title}`}
      >
        {buttonText}
      </button>
    </div>
  </div>
);

interface LogAndTrackScreenProps {
  onBack: () => void;
  onNavigateToNutrition?: () => void;
}

const LogAndTrackScreen: React.FC<LogAndTrackScreenProps> = ({ 
  onBack,
  onNavigateToNutrition 
}) => {
  const [currentView, setCurrentView] = useState<'main' | 'symptom-logging'>('main');
  const [currentStep, setCurrentStep] = useState(1);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedSymptoms, setSelectedSymptoms] = useState<string[]>([]);
  const [showAllSymptoms, setShowAllSymptoms] = useState(false);
  const [isMultiLogging, setIsMultiLogging] = useState(false);
  const [currentSymptomIndex, setCurrentSymptomIndex] = useState(0);
  const [multiSymptomData, setMultiSymptomData] = useState<Array<{
    symptom: string;
    severity: number;
    notes: string;
    date: string;
  }>>([]);
  const [recentEntries, setRecentEntries] = useState<Array<{
    id: string;
    symptom: string;
    severity: number;
    timestamp: string;
    notes?: string;
  }>>([
    {
      id: '1',
      symptom: 'Fatigue',
      severity: 3,
      timestamp: 'Today, 2:30 PM',
      notes: 'Felt tired after lunch'
    },
    {
      id: '2',
      symptom: 'Headache',
      severity: 2,
      timestamp: 'Yesterday, 4:15 PM'
    }
  ]);

  // Mock data for symptoms
  const allSymptoms = [
    'Fatigue', 'Headache', 'Nausea', 'Dizziness', 'Cramps', 'Bloating',
    'Mood swings', 'Anxiety', 'Back pain', 'Breast tenderness', 'Acne',
    'Food cravings', 'Insomnia', 'Hot flashes', 'Joint pain'
  ];

  const quickLogSymptoms = [
    'Fatigue', 'Headache', 'Cramps', 'Bloating', 'Mood swings'
  ];

  const commonSymptoms = [
    'Nausea', 'Dizziness', 'Anxiety', 'Back pain', 'Food cravings'
  ];

  const handleLogSymptom = () => {
    setCurrentView('symptom-logging');
    setCurrentStep(1);
    setSelectedSymptoms([]);
    setSearchQuery('');
  };

  const handleViewNutritionPlan = () => {
    if (onNavigateToNutrition) {
      onNavigateToNutrition();
    }
  };

  const handleLogMedication = () => {
    // This would navigate to the new medication logging flow (to be built)
    console.log('Navigate to medication logging flow');
  };

  const handleBackToMain = () => {
    setCurrentView('main');
    setCurrentStep(1);
    setSelectedSymptoms([]);
    setSearchQuery('');
    setIsMultiLogging(false);
    setCurrentSymptomIndex(0);
    setMultiSymptomData([]);
  };

  const handleSymptomSelect = (symptom: string) => {
    if (selectedSymptoms.includes(symptom)) {
      setSelectedSymptoms(selectedSymptoms.filter(s => s !== symptom));
    } else {
      setSelectedSymptoms([...selectedSymptoms, symptom]);
    }
  };

  const handleNextStep = () => {
    if (selectedSymptoms.length === 0) return;

    if (selectedSymptoms.length === 1) {
      setCurrentStep(2);
      setMultiSymptomData([{
        symptom: selectedSymptoms[0],
        severity: 3,
        notes: '',
        date: new Date().toISOString().split('T')[0]
      }]);
    } else {
      setIsMultiLogging(true);
    setCurrentStep(2);
      setMultiSymptomData(selectedSymptoms.map(symptom => ({
        symptom,
        severity: 3,
        notes: '',
        date: new Date().toISOString().split('T')[0]
      })));
    }
  };

  const handleMultiSymptomNext = () => {
    if (currentSymptomIndex < multiSymptomData.length - 1) {
      setCurrentSymptomIndex(currentSymptomIndex + 1);
    } else {
      setCurrentStep(3);
    }
  };

  const handleMultiSymptomBack = () => {
    if (currentSymptomIndex > 0) {
      setCurrentSymptomIndex(currentSymptomIndex - 1);
    } else {
      setCurrentStep(1);
      setIsMultiLogging(false);
    }
  };

  const handleBackToStep1 = () => {
    setCurrentStep(1);
    setIsMultiLogging(false);
    setCurrentSymptomIndex(0);
  };

  const updateMultiSymptomData = (field: string, value: any) => {
    const updated = [...multiSymptomData];
    updated[currentSymptomIndex] = {
      ...updated[currentSymptomIndex],
      [field]: value
    };
    setMultiSymptomData(updated);
  };

  const handleSaveSymptom = () => {
    const newEntry = {
      id: Date.now().toString(),
      symptom: multiSymptomData[0].symptom,
      severity: multiSymptomData[0].severity,
      timestamp: new Date().toLocaleString(),
      notes: multiSymptomData[0].notes || undefined
    };
    setRecentEntries([newEntry, ...recentEntries]);
    setCurrentStep(3);
  };

  const handleSaveMultiSymptoms = () => {
    const newEntries = multiSymptomData.map((data, index) => ({
      id: (Date.now() + index).toString(),
      symptom: data.symptom,
      severity: data.severity,
      timestamp: new Date().toLocaleString(),
      notes: data.notes || undefined
    }));
    setRecentEntries([...newEntries, ...recentEntries]);
    setCurrentStep(3);
  };

  const filteredSymptoms = allSymptoms.filter(symptom =>
    symptom.toLowerCase().includes(searchQuery.toLowerCase())
  );

  if (currentView === 'symptom-logging') {
  return (
    <div className="min-h-screen bg-gray-50">
        {/* Header */}
        <header className="bg-white shadow-sm border-b border-gray-200 px-4 py-4">
          <div className="flex items-center justify-between">
              <button
              onClick={currentStep === 1 ? handleBackToMain : (isMultiLogging && currentSymptomIndex > 0 ? handleMultiSymptomBack : handleBackToStep1)}
              className="p-2 text-gray-400 hover:text-gray-600 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 rounded-lg min-h-[44px] min-w-[44px] flex items-center justify-center"
              aria-label={currentStep === 1 ? "Go back" : "Previous"}
              >
                <BackArrowIcon />
              </button>
            <div className="flex-1 text-center">
              <h1 className="text-xl font-semibold text-gray-900">Symptom Tracker</h1>
            </div>
            <div className="w-10"></div>
          </div>
        </header>

        {/* Progress Bar */}
        <div className="bg-white px-4 py-3 border-b border-gray-200">
          <div className="max-w-md mx-auto">
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div 
                className="bg-indigo-600 h-2 rounded-full transition-all duration-300 ease-out"
                style={{ width: `${(currentStep / 3) * 100}%` }}
              ></div>
          </div>
        </div>
      </div>

        {/* Main Content */}
        <main className="px-4 py-6">
        {currentStep === 1 && (
            <div className="max-w-2xl mx-auto space-y-6">
              {/* Quick Log Section */}
              <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
                <h2 className="text-lg font-semibold text-gray-900 mb-4">Quick Log</h2>

                {/* Your Recent */}
                <div className="mb-6">
                  <h3 className="text-sm font-medium text-gray-700 mb-3">Your Recent</h3>
                  <div className="flex flex-wrap gap-2">
                    {quickLogSymptoms.map(symptom => (
                    <button
                      key={symptom}
                      onClick={() => handleSymptomSelect(symptom)}
                        className={`px-3 py-2 rounded-full text-sm font-medium transition-colors ${
                          selectedSymptoms.includes(symptom)
                            ? 'bg-indigo-600 text-white'
                            : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                        }`}
                    >
                      {symptom}
                    </button>
                  ))}
                </div>
              </div>

                {/* Common Today */}
                <div className="mb-6">
                  <h3 className="text-sm font-medium text-gray-700 mb-3">Common Today</h3>
                  <div className="flex flex-wrap gap-2">
                    {commonSymptoms.map(symptom => (
                    <button
                      key={symptom}
                      onClick={() => handleSymptomSelect(symptom)}
                        className={`px-3 py-2 rounded-full text-sm font-medium transition-colors ${
                          selectedSymptoms.includes(symptom)
                            ? 'bg-indigo-600 text-white'
                            : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                        }`}
                    >
                      {symptom}
                    </button>
                  ))}
                </div>
                </div>

                {/* Search Bar */}
                <div className="mb-6">
                  <div className="relative">
                    <input
                      type="text"
                      placeholder="Search symptoms..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                    />
                  </div>

                  {/* Search Results */}
                  {searchQuery && (
                    <div className="mt-3 bg-white border border-gray-200 rounded-lg shadow-lg max-h-60 overflow-y-auto">
                      {filteredSymptoms.length > 0 ? (
                        filteredSymptoms.map(symptom => (
                          <button
                            key={symptom}
                            onClick={() => handleSymptomSelect(symptom)}
                            className={`w-full text-left px-4 py-3 hover:bg-gray-50 transition-colors ${
                              selectedSymptoms.includes(symptom) ? 'bg-indigo-50' : ''
                            }`}
                          >
                            <span className={`font-medium ${
                              selectedSymptoms.includes(symptom) ? 'text-indigo-600' : 'text-gray-900'
                            }`}>
                              {symptom}
                            </span>
                          </button>
                        ))
                      ) : (
                        <div className="px-4 py-3 text-gray-500">
                          No symptoms found
                        </div>
                      )}
              </div>
            )}
                </div>

                {/* Show All Symptoms Toggle */}
                <div className="mb-6">
                  <button
                    onClick={() => setShowAllSymptoms(!showAllSymptoms)}
                    className="text-indigo-600 hover:text-indigo-700 font-medium text-sm"
                  >
                    {showAllSymptoms ? 'Hide' : 'Show'} all symptoms
                  </button>

                  {showAllSymptoms && (
                    <div className="mt-3 bg-gray-50 rounded-lg p-4">
                      <div className="flex flex-wrap gap-2">
                        {allSymptoms.map(symptom => (
                          <button
                            key={symptom}
                            onClick={() => handleSymptomSelect(symptom)}
                            className={`px-3 py-2 rounded-full text-sm font-medium transition-colors ${
                              selectedSymptoms.includes(symptom)
                                ? 'bg-indigo-600 text-white'
                                : 'bg-white text-gray-700 hover:bg-gray-100 border border-gray-200'
                            }`}
                          >
                            {symptom}
                          </button>
                        ))}
                      </div>
                    </div>
                  )}
            </div>

                {/* Next Button */}
              <button
                  onClick={handleNextStep}
                  disabled={selectedSymptoms.length === 0}
                  className={`w-full py-3 px-4 rounded-lg font-medium transition-colors ${
                    selectedSymptoms.length > 0
                      ? 'bg-indigo-600 text-white hover:bg-indigo-700 active:bg-indigo-800'
                      : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  }`}
                >
                  Next ({selectedSymptoms.length} selected)
              </button>
            </div>

              {/* Jamie's Care Snapshot */}
              <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl border border-blue-200 p-6">
                <h3 className="text-lg font-semibold text-blue-900 mb-2">Jamie's Care Snapshot</h3>
                <p className="text-blue-800 text-sm">
                  Jamie has logged Fatigue 3 times in the last 2 weeks. Consider reviewing sleep patterns and nutrition.
                </p>
            </div>

              {/* Recent Entries */}
              <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-semibold text-gray-900">Recent Entries</h3>
                  <select className="text-sm text-gray-600 border border-gray-300 rounded px-2 py-1">
                    <option>Past 7 days</option>
                    <option>Past 30 days</option>
                    <option>Past 6 months</option>
                  </select>
                </div>

                <div className="space-y-3">
                  {recentEntries.slice(0, 3).map(entry => (
                    <div key={entry.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex-1">
                        <div className="flex items-center space-x-2">
                          <span className="font-medium text-gray-900">{entry.symptom}</span>
                          <span className="text-sm text-gray-500">•</span>
                          <span className="text-sm text-gray-500">{entry.timestamp}</span>
                        </div>
                        {entry.notes && (
                          <p className="text-sm text-gray-600 mt-1">{entry.notes}</p>
                        )}
                      </div>
                      <div className="flex items-center space-x-2">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          entry.severity >= 4 ? 'bg-red-100 text-red-800' :
                          entry.severity >= 3 ? 'bg-yellow-100 text-yellow-800' :
                          'bg-green-100 text-green-800'
                        }`}>
                          Level {entry.severity}
                    </span>
                  </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}

          {currentStep === 2 && (
            <div className="max-w-2xl mx-auto">
              <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
                <div className="mb-6">
                  <h2 className="text-lg font-semibold text-gray-900 mb-2">
                    {isMultiLogging ? `Symptom ${currentSymptomIndex + 1} of ${multiSymptomData.length}` : 'Symptom Details'}
                  </h2>
                  <p className="text-gray-600">
                    {isMultiLogging ? multiSymptomData[currentSymptomIndex].symptom : multiSymptomData[0].symptom}
              </p>
            </div>

                {/* Severity Slider */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Severity Level: {isMultiLogging ? multiSymptomData[currentSymptomIndex].severity : multiSymptomData[0].severity}
                  </label>
                  <input
                    type="range"
                    min="1"
                    max="5"
                    value={isMultiLogging ? multiSymptomData[currentSymptomIndex].severity : multiSymptomData[0].severity}
                    onChange={(e) => updateMultiSymptomData('severity', parseInt(e.target.value))}
                    className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                  />
                  <div className="flex justify-between text-xs text-gray-500 mt-1">
                    <span>Mild</span>
                    <span>Severe</span>
                  </div>
              </div>

                {/* Notes */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Notes (optional)
                  </label>
                  <textarea
                    value={isMultiLogging ? multiSymptomData[currentSymptomIndex].notes : multiSymptomData[0].notes}
                    onChange={(e) => updateMultiSymptomData('notes', e.target.value)}
                    placeholder="Add any additional details..."
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                    rows={3}
                      />
                    </div>

                {/* Date */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    When did this start?
                  </label>
                      <input
                    type="date"
                    value={isMultiLogging ? multiSymptomData[currentSymptomIndex].date : multiSymptomData[0].date}
                    onChange={(e) => updateMultiSymptomData('date', e.target.value)}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                      />
            </div>

            {/* Action Buttons */}
                <div className="flex space-x-3">
              <button
                    onClick={isMultiLogging ? handleMultiSymptomNext : handleSaveSymptom}
                    className="flex-1 bg-indigo-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-indigo-700 active:bg-indigo-800 transition-colors"
                  >
                    {isMultiLogging ? 'Next Symptom' : 'Save Symptom'}
              </button>
            </div>
              </div>
            </div>
          )}

          {currentStep === 3 && (
            <div className="max-w-2xl mx-auto">
              <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm text-center">
                <div className="text-green-500 mb-4">
                  <CheckIcon />
                </div>
                <h2 className="text-xl font-semibold text-gray-900 mb-2">
                  {isMultiLogging ? 'Symptoms Logged Successfully!' : 'Symptom Logged Successfully!'}
                </h2>
                <p className="text-gray-600 mb-6">
                  {isMultiLogging 
                    ? `You've logged ${multiSymptomData.length} symptoms. Keep tracking to see patterns over time.`
                    : 'Your symptom has been logged. Keep tracking to see patterns over time.'
                  }
                </p>
                <button
                  onClick={handleBackToMain}
                  className="bg-indigo-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-indigo-700 active:bg-indigo-800 transition-colors"
                >
                  Back to Log & Track
                </button>
            </div>
          </div>
        )}
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200 px-4 py-4">
            <div className="flex items-center justify-between">
              <button 
            onClick={onBack}
            className="p-2 text-gray-400 hover:text-gray-600 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 rounded-lg min-h-[44px] min-w-[44px] flex items-center justify-center"
            aria-label="Go back"
              >
            <BackArrowIcon />
              </button>
          <div className="flex-1 text-center">
            <h1 className="text-xl font-semibold text-gray-900">Log & Track</h1>
          </div>
          <div className="w-10"></div> {/* Spacer for centering */}
                </div>
      </header>

      {/* Main Content */}
      <main className="px-4 py-8">
        {/* Three Unified Tracker Cards */}
        <div className="space-y-6 max-w-2xl mx-auto">
          <TrackerCard
            icon="🙂"
            title="Symptom Status"
            status="No new symptoms logged"
            buttonText="Log Symptom"
            onClick={handleLogSymptom}
          />

          <TrackerCard
            icon="🍽️"
            title="Nutrition Plan"
            status="Today's plan ready"
            buttonText="View Plan"
            onClick={handleViewNutritionPlan}
          />

          <TrackerCard
            icon="💊"
            title="Medication Tracker"
            status="No medication logged today"
            buttonText="Log Medication"
            onClick={handleLogMedication}
          />
        </div>
      </main>
    </div>
  );
};

export default LogAndTrackScreen;
