import React, { useState } from 'react';

interface GrowthData {
  date: string;
  height: number;
  weight: number;
  notes: string;
}

interface DevelopmentMilestone {
  id: string;
  title: string;
  description: string;
  achieved: boolean;
  date?: string;
}

interface GrowthDevelopmentScreenProps {
  onBack?: () => void;
  onNavigateToNutrition?: () => void;
  onNavigateToSymptomLogs?: () => void;
}

const GrowthDevelopmentScreen: React.FC<GrowthDevelopmentScreenProps> = ({ 
  onBack,
  onNavigateToNutrition,
  onNavigateToSymptomLogs
}) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('overview');

  const [growthData] = useState<GrowthData[]>([
    { date: '2024-01-15', height: 165, weight: 58, notes: 'Regular checkup' },
    { date: '2024-02-15', height: 166, weight: 59, notes: 'Good progress' },
    { date: '2024-03-15', height: 167, weight: 60, notes: 'Steady growth' },
  ]);

  const [milestones] = useState<DevelopmentMilestone[]>([
    { id: '1', title: 'Regular Exercise', description: 'Maintain 30 min daily activity', achieved: true, date: '2024-01-10' },
    { id: '2', title: 'Balanced Nutrition', description: 'Follow recommended meal plan', achieved: true, date: '2024-02-01' },
    { id: '3', title: 'Stress Management', description: 'Practice relaxation techniques', achieved: false },
    { id: '4', title: 'Sleep Quality', description: 'Maintain 7-8 hours sleep', achieved: false },
  ]);

  const navigationItems = [
    { id: 'dashboard', label: 'Dashboard', icon: 'ðŸ“Š' },
    { id: 'nutrition', label: 'Nutrition', icon: 'ðŸ¥—' },
    { id: 'milestones', label: 'Milestones', icon: 'ðŸŽ¯', active: true },
    { id: 'resources', label: 'Resources', icon: 'ðŸ“š' },
    { id: 'community', label: 'Community', icon: 'ðŸ‘¥' },
    { id: 'profile', label: 'Profile', icon: 'ðŸ‘¤' },
  ];

  const sidebarItems = [
    { id: 'overview', label: 'Progress Overview', icon: 'ðŸ“ˆ' },
    { id: 'input', label: 'Input & Journaling', icon: 'âœï¸' },
    { id: 'recommendations', label: 'Recommendations', icon: 'ðŸ’¡' },
    { id: 'goals', label: 'Goals & Personalization', icon: 'ðŸŽ¯' },
    { id: 'reports', label: 'Reports & Sharing', icon: 'ðŸ“‹' },
    { id: 'community', label: 'Community & Feedback', icon: 'ðŸ’¬' },
    { id: 'references', label: 'References & Transparency', icon: 'ðŸ”—' },
  ];

  const renderMainContent = () => {
    switch (activeSection) {
      case 'overview':
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900">Progress Overview</h2>
            {/* Growth Tracking */}
            <div className="bg-white rounded-xl border border-gray-200 p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Growth Tracking</h3>
              <div className="grid grid-cols-2 gap-4 mb-4">
                <div className="text-center">
                  <div className="text-2xl font-bold text-indigo-600">167 cm</div>
                  <div className="text-sm text-gray-600">Current Height</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-green-600">60 kg</div>
                  <div className="text-sm text-gray-600">Current Weight</div>
                </div>
              </div>
              
              <div className="space-y-3">
                {growthData.map((data, index) => (
                  <div key={index} className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                    <div>
                      <div className="font-medium text-gray-900">{data.date}</div>
                      <div className="text-sm text-gray-600">{data.notes}</div>
                    </div>
                    <div className="text-right">
                      <div className="font-medium">{data.height} cm</div>
                      <div className="text-sm text-gray-600">{data.weight} kg</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        );
      case 'input':
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900">Input & Journaling</h2>
            <div className="bg-white rounded-xl border border-gray-200 p-6">
              <p className="text-gray-600">Journal your daily progress and observations here.</p>
            </div>
          </div>
        );
      case 'recommendations':
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900">Recommendations</h2>
            <div className="bg-white rounded-xl border border-gray-200 p-6">
              <p className="text-gray-600">Personalized recommendations based on your progress.</p>
            </div>
          </div>
        );
      case 'goals':
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900">Goals & Personalization</h2>
            <div className="bg-white rounded-xl border border-gray-200 p-6">
              <p className="text-gray-600">Set and track your personal development goals.</p>
            </div>
          </div>
        );
      case 'reports':
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900">Reports & Sharing</h2>
            <div className="bg-white rounded-xl border border-gray-200 p-6">
              <p className="text-gray-600">Generate reports and share your progress.</p>
            </div>
          </div>
        );
      case 'community':
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900">Community & Feedback</h2>
            <div className="bg-white rounded-xl border border-gray-200 p-6">
              <p className="text-gray-600">Connect with the community and get feedback.</p>
            </div>
          </div>
        );
      case 'references':
        return (
          <div className="space-y-6">
            <h2 className="text-xl font-semibold text-gray-900">References & Transparency</h2>
            <div className="bg-white rounded-xl border border-gray-200 p-6">
              <p className="text-gray-600">View references and transparency information.</p>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Top Navigation Bar */}
      <nav className="bg-white border-b border-gray-200 px-4 py-3">
        <div className="flex items-center justify-between">
          {/* Left side - Logo and main navigation */}
          <div className="flex items-center space-x-6">
            {/* App Logo/Home */}
            <div className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-indigo-600 rounded-lg flex items-center justify-center">
                <span className="text-white font-bold text-sm">CG</span>
              </div>
              <span className="font-semibold text-gray-900">CareGene</span>
            </div>

            {/* Desktop Main Navigation */}
            <div className="hidden md:flex items-center space-x-1">
              {navigationItems.map((item) => (
                <button
                  key={item.id}
                  className={`px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                    item.active 
                      ? 'bg-indigo-100 text-indigo-700' 
                      : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                  }`}
                >
                  <span className="mr-1">{item.icon}</span>
                  {item.label}
                </button>
              ))}
            </div>
          </div>

          {/* Right side - Quick actions */}
          <div className="flex items-center space-x-3">
            <button className="text-gray-600 hover:text-gray-900 p-2">
              <span className="text-lg">ðŸ“¤</span>
            </button>
            <button className="text-gray-600 hover:text-gray-900 p-2">
              <span className="text-lg">ðŸ’¬</span>
            </button>
            
            {/* Mobile hamburger menu */}
            <button
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              className="md:hidden text-gray-600 hover:text-gray-900 p-2"
            >
              <span className="text-lg">â˜°</span>
            </button>
          </div>
        </div>

        {/* Mobile Navigation Menu */}
        {isMobileMenuOpen && (
          <div className="md:hidden mt-3 pb-3 border-t border-gray-200">
            <div className="pt-3 space-y-1">
              {navigationItems.map((item) => (
                <button
                  key={item.id}
                  className={`w-full text-left px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                    item.active 
                      ? 'bg-indigo-100 text-indigo-700' 
                      : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                  }`}
                >
                  <span className="mr-2">{item.icon}</span>
                  {item.label}
                </button>
              ))}
            </div>
          </div>
        )}
      </nav>

      <div className="flex">
        {/* Sidebar - Desktop/Tablet */}
        <div className="hidden lg:block w-64 bg-white border-r border-gray-200 min-h-screen">
          <div className="p-4">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Growth & Development</h2>
            <nav className="space-y-1">
              {sidebarItems.map((item) => (
                <button
                  key={item.id}
                  onClick={() => setActiveSection(item.id)}
                  className={`w-full text-left px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                    activeSection === item.id
                      ? 'bg-indigo-100 text-indigo-700'
                      : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                  }`}
                >
                  <span className="mr-2">{item.icon}</span>
                  {item.label}
                </button>
              ))}
            </nav>
          </div>
        </div>

        {/* Main Content Area */}
        <div className="flex-1">
          <div className="p-6">
            {/* Page Header */}
            <div className="mb-6">
              <h1 className="text-3xl font-bold text-gray-900">Growth & Development Milestones</h1>
              <p className="text-gray-600 mt-2">Track your progress and achieve your development goals</p>
            </div>

            {/* Mobile Sidebar Toggle */}
            <div className="lg:hidden mb-4">
              <button
                onClick={() => setIsSidebarOpen(!isSidebarOpen)}
                className="flex items-center space-x-2 text-gray-600 hover:text-gray-900"
              >
                <span>â˜°</span>
                <span>Menu</span>
              </button>
            </div>

            {/* Mobile Sidebar Overlay */}
            {isSidebarOpen && (
              <div className="lg:hidden fixed inset-0 z-50">
                <div className="fixed inset-0 bg-black bg-opacity-50" onClick={() => setIsSidebarOpen(false)}></div>
                <div className="fixed left-0 top-0 h-full w-64 bg-white border-r border-gray-200 p-4">
                  <div className="flex items-center justify-between mb-4">
                    <h2 className="text-lg font-semibold text-gray-900">Menu</h2>
                    <button onClick={() => setIsSidebarOpen(false)} className="text-gray-600 hover:text-gray-900">
                      âœ•
                    </button>
                  </div>
                  <nav className="space-y-1">
                    {sidebarItems.map((item) => (
                      <button
                        key={item.id}
                        onClick={() => {
                          setActiveSection(item.id);
                          setIsSidebarOpen(false);
                        }}
                        className={`w-full text-left px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                          activeSection === item.id
                            ? 'bg-indigo-100 text-indigo-700'
                            : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                        }`}
                      >
                        <span className="mr-2">{item.icon}</span>
                        {item.label}
                      </button>
                    ))}
                  </nav>
                </div>
              </div>
            )}

            {/* Content */}
            {renderMainContent()}

            {/* Development Milestones Section */}
            <div className="mt-8">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Development Milestones</h2>
              <div className="space-y-3">
                {milestones.map((milestone) => (
                  <div key={milestone.id} className="bg-white rounded-xl border border-gray-200 p-4">
                    <div className="flex items-start justify-between">
                      <div className="flex items-center space-x-3">
                        <div className={`w-6 h-6 rounded-full flex items-center justify-center ${
                          milestone.achieved 
                            ? 'bg-green-100 text-green-600' 
                            : 'bg-gray-100 text-gray-400'
                        }`}>
                          {milestone.achieved ? 'âœ“' : 'â—‹'}
                        </div>
                        <div className="flex-1">
                          <h3 className="font-medium text-gray-900">{milestone.title}</h3>
                          <p className="text-sm text-gray-600">{milestone.description}</p>
                          {milestone.date && (
                            <p className="text-xs text-gray-500 mt-1">Achieved: {milestone.date}</p>
                          )}
                        </div>
                      </div>
                      <button className="text-indigo-600 hover:text-indigo-700 text-sm font-medium">
                        {milestone.achieved ? 'View Details' : 'Mark Complete'}
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Add New Entry Button */}
            <div className="mt-8 text-center">
              <button className="bg-indigo-600 text-white px-6 py-3 rounded-lg font-medium hover:bg-indigo-700 transition-colors">
                Add New Growth Entry
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GrowthDevelopmentScreen;
