import React, { useState } from 'react';

interface GrowthData {
  date: string;
  height: number;
  weight: number;
  notes: string;
}

interface DevelopmentMilestone {
  id: string;
  title: string;
  description: string;
  achieved: boolean;
  date?: string;
}

interface ChildProfile {
  name: string;
  age: string;
  condition: string;
  lastUpdated: string;
}

interface GrowthDevelopmentScreenProps {
  onBack?: () => void;
  onNavigateToNutrition?: () => void;
  onNavigateToSymptomLogs?: () => void;
}

const GrowthDevelopmentScreen: React.FC<GrowthDevelopmentScreenProps> = ({ 
  onBack,
  onNavigateToNutrition,
  onNavigateToSymptomLogs
}) => {
  // Mock child profile data
  const [childProfile] = useState<ChildProfile>({
    name: "Emma Johnson",
    age: "8 years, 3 months",
    condition: "Down Syndrome",
    lastUpdated: "2024-03-15"
  });

  const [growthData] = useState<GrowthData[]>([
    { date: '2024-01-15', height: 165, weight: 58, notes: 'Regular checkup' },
    { date: '2024-02-15', height: 166, weight: 59, notes: 'Good progress' },
    { date: '2024-03-15', height: 167, weight: 60, notes: 'Steady growth' },
  ]);

  const [milestones] = useState<DevelopmentMilestone[]>([
    { id: '1', title: 'Regular Exercise', description: 'Maintain 30 min daily activity', achieved: true, date: '2024-01-10' },
    { id: '2', title: 'Balanced Nutrition', description: 'Follow recommended meal plan', achieved: true, date: '2024-02-01' },
    { id: '3', title: 'Stress Management', description: 'Practice relaxation techniques', achieved: false },
    { id: '4', title: 'Sleep Quality', description: 'Maintain 7-8 hours sleep', achieved: false },
  ]);

  return (
    <div className="min-h-screen bg-gray-50 py-6 px-4">
      {/* Responsive Container */}
      <div className="max-w-4xl mx-auto">
        {/* Main Card Container */}
        <div className="bg-white rounded-2xl shadow-lg overflow-hidden">
          {/* Enhanced Header */}
          <div className="bg-gradient-to-r from-indigo-600 to-purple-600 px-6 py-8">
            <div className="flex items-center justify-between mb-4">
              <h1 className="text-3xl font-bold text-white">Growth & Development</h1>
              {onBack && (
                <button
                  onClick={onBack}
                  className="text-white hover:text-indigo-200 font-medium flex items-center space-x-2 transition-colors"
                >
                  <span>←</span>
                  <span>Back</span>
                </button>
              )}
            </div>

            {/* Child Profile Information */}
            <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4">
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4 text-white">
                <div>
                  <div className="text-sm font-medium text-indigo-200">Child's Name</div>
                  <div className="text-lg font-semibold">{childProfile.name}</div>
                </div>
                <div>
                  <div className="text-sm font-medium text-indigo-200">Age</div>
                  <div className="text-lg font-semibold">{childProfile.age}</div>
                </div>
                <div>
                  <div className="text-sm font-medium text-indigo-200">Condition</div>
                  <div className="text-lg font-semibold">{childProfile.condition}</div>
                </div>
                <div>
                  <div className="text-sm font-medium text-indigo-200">Last Updated</div>
                  <div className="text-lg font-semibold">{childProfile.lastUpdated}</div>
                </div>
              </div>
            </div>
          </div>

          {/* Scrollable Content Area */}
          <div className="max-h-[calc(100vh-200px)] overflow-y-auto">
            <div className="p-6 space-y-8">
              {/* Growth Tracking */}
              <div>
                <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                  <span className="w-2 h-6 bg-indigo-600 rounded-full mr-3"></span>
                  Growth Tracking
                </h2>
                <div className="bg-gray-50 rounded-xl border border-gray-200 p-6">
                  <div className="grid grid-cols-2 gap-6 mb-6">
                    <div className="text-center bg-white rounded-lg p-4 shadow-sm">
                      <div className="text-3xl font-bold text-indigo-600">167 cm</div>
                      <div className="text-sm text-gray-600 mt-1">Current Height</div>
                      <div className="text-xs text-green-600 mt-1">+2 cm this month</div>
                    </div>
                    <div className="text-center bg-white rounded-lg p-4 shadow-sm">
                      <div className="text-3xl font-bold text-green-600">60 kg</div>
                      <div className="text-sm text-gray-600 mt-1">Current Weight</div>
                      <div className="text-xs text-green-600 mt-1">+1 kg this month</div>
                    </div>
                  </div>

                  <div className="space-y-3">
                    <h3 className="font-medium text-gray-900 mb-3">Growth History</h3>
                    {growthData.map((data, index) => (
                      <div key={index} className="flex justify-between items-center p-4 bg-white rounded-lg shadow-sm border border-gray-100">
                        <div>
                          <div className="font-medium text-gray-900">{data.date}</div>
                          <div className="text-sm text-gray-600">{data.notes}</div>
                        </div>
                        <div className="text-right">
                          <div className="font-medium text-indigo-600">{data.height} cm</div>
                          <div className="text-sm text-gray-600">{data.weight} kg</div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              {/* Development Milestones */}
              <div>
                <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                  <span className="w-2 h-6 bg-green-600 rounded-full mr-3"></span>
                  Development Milestones
                </h2>
                <div className="space-y-4">
                  {milestones.map((milestone) => (
                    <div key={milestone.id} className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm hover:shadow-md transition-shadow">
                      <div className="flex items-start justify-between">
                        <div className="flex items-center space-x-4">
                          <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                            milestone.achieved 
                              ? 'bg-green-100 text-green-600' 
                              : 'bg-gray-100 text-gray-400'
                          }`}>
                            {milestone.achieved ? '✓' : '○'}
                          </div>
                          <div className="flex-1">
                            <h3 className="font-semibold text-gray-900 text-lg">{milestone.title}</h3>
                            <p className="text-gray-600 mt-1">{milestone.description}</p>
                            {milestone.date && (
                              <p className="text-sm text-green-600 mt-2 font-medium">✓ Achieved: {milestone.date}</p>
                            )}
                          </div>
                        </div>
                        <button className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                          milestone.achieved 
                            ? 'bg-indigo-100 text-indigo-700 hover:bg-indigo-200' 
                            : 'bg-green-100 text-green-700 hover:bg-green-200'
                        }`}>
                          {milestone.achieved ? 'View Details' : 'Mark Complete'}
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Add New Entry Button */}
              <div className="text-center pt-4">
                <button className="bg-indigo-600 text-white px-8 py-4 rounded-xl font-semibold hover:bg-indigo-700 transition-colors shadow-lg hover:shadow-xl">
                  + Add New Growth Entry
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GrowthDevelopmentScreen;
