                         {reminder.notes && (
                           <p className="text-xs text-gray-400 mt-1">{reminder.notes}</p>
                         )}
                       </div>
                     </div>
                     
                     <div className="flex-shrink-0">
                       {reminder.type === 'medication' ? (
                         <button
                           onClick={() => handleMarkAsTaken(reminder.id)}
                           className="px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 transition-colors"
                         >
                           Mark as taken
                         </button>
                       ) : (
                         <button
                           onClick={() => handleViewAppointmentDetails(reminder.id)}
                           className="px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors"
                         >
                           View details
                         </button>
                       )}
                     </div>
                   </div>
                 </div>
               ))
             ) : (
               <div className="bg-white border border-gray-200 rounded-lg p-8 text-center">
                 <div className="w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-3">
                   <svg className="w-6 h-6 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                     <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                   </svg>
                 </div>
                 <p className="text-sm text-gray-500">No reminders scheduled for today.</p>
               </div>
             )}
           </div>
         </div>

         {/* Jamie's Care Snapshot */}
        <div className="bg-gradient-to-r from-indigo-50 to-purple-50 border border-indigo-200 rounded-xl p-4 shadow-sm mb-8" role="region" aria-label="Personalized care insights">
          <div className="flex items-start space-x-3">
            <div className="flex-shrink-0 w-8 h-8 bg-indigo-100 rounded-full flex items-center justify-center" aria-hidden="true">
              <svg className="w-4 h-4 text-indigo-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
              </svg>
            </div>
            <div className="flex-1 min-w-0">
              <h3 className="text-sm font-semibold text-indigo-900 mb-2">Jamie's Care Snapshot</h3>
              <div className="space-y-2 text-sm text-indigo-800">
                <p>â€¢ Jamie has logged <span className="font-semibold text-indigo-900">medications</span> 8 times in the last 2 weeks</p>
                <p>â€¢ <span className="font-semibold text-indigo-900">Iron supplement</span> compliance is at 85% this week</p>
                <p>â€¢ <span className="font-semibold text-indigo-900">Vitamin D</span> is being taken consistently</p>
              </div>
            </div>
          </div>
        </div>







        {/* AI-Powered Suggestion/Tip Banner */}
        {showAISuggestion && (
          <div className="mb-8">
            <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-lg p-4 border border-blue-100 relative">
              <button
                onClick={() => setShowAISuggestion(false)}
                className="absolute top-2 right-2 text-blue-400 hover:text-blue-600 transition-colors"
                aria-label="Dismiss suggestion"
              >
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
              <div className="flex items-start space-x-3">
                <div className="flex-shrink-0 w-6 h-6 bg-blue-100 rounded-full flex items-center justify-center" aria-hidden="true">
                  <svg className="w-3 h-3 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                </div>
                <div className="flex-1 min-w-0">
                  <h4 className="text-sm font-medium text-blue-900 mb-1">Medication Reminder</h4>
                  <p className="text-sm text-blue-800">
                    Set up medication reminders to ensure consistent dosing. You can also track side effects and effectiveness over time.
                  </p>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Recent Entries Section - Always visible below main content */}
      <div className="max-w-2xl mx-auto px-4 sm:px-6 pb-8">
        <div className="bg-white rounded-xl border border-gray-200 shadow-lg">
          {/* Section Header */}
          <div className="px-6 py-4 border-b border-gray-100">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <span className="text-lg font-semibold text-gray-900">Recent Entries</span>
                <span className="text-sm text-gray-500">({medicationHistory.length} entries)</span>
              </div>
              <div className="relative">
                <select 
                  value={historyTimeFilter}
                  onChange={(e) => handleHistoryTimeFilterChange(e.target.value)}
                  className="appearance-none bg-white border border-gray-300 rounded-lg px-3 py-3 pr-8 text-sm font-medium text-gray-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 cursor-pointer hover:border-gray-400 transition-colors min-h-[44px]"
                  aria-label="Filter entries by time period"
                >
                  <option value="7days">Past 7 days</option>
                  <option value="30days">Past 30 days</option>
                  <option value="6months">Past 6 months</option>
                  <option value="1year">Past 1 year</option>
                  <option value="custom">Custom</option>
                </select>
                <div className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
                  <svg className="w-4 h-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                  </svg>
                </div>
              </div>
            </div>
          </div>

          {/* Recent Entries Timeline */}
          <div className="divide-y divide-gray-100">
            {getFilteredHistory().slice(0, 4).map((entry) => (
              <div key={entry.id} className="px-6 py-4 hover:bg-gray-50 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2">
                <div className="flex items-start space-x-3">
                  {/* Item Icon */}
                  <div className="flex-shrink-0 mt-1">
                    <span className="text-xl">{entry.icon}</span>
                  </div>
                  
                  {/* Entry Details */}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between mb-1">
                      <div className="flex items-center space-x-2">
                        <h5 className="text-sm font-medium text-gray-900">
                          {entry.medicationName}
                        </h5>
                        <span className={`inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium ${
                          entry.status === 'taken' ? 'bg-green-100 text-green-700' :
                          entry.status === 'missed' ? 'bg-red-100 text-red-700' :
                          'bg-yellow-100 text-yellow-700'
                        }`}>
                          {entry.status}
                        </span>
                      </div>
                      <span className="text-xs text-gray-500 flex-shrink-0 ml-2">
