import React, { useState, useEffect } from 'react';

interface SymptomNutrientInsightsProps {
  onBack: () => void;
  symptomName?: string;
  severity?: number;
  date?: string;
}

interface SymptomEntry {
  id: string;
  symptom: string;
  severity: number;
  duration: string;
  triggers: string[];
  notes: string;
  timestamp: Date;
  domain: 'motor' | 'communication' | 'social' | 'sensory' | 'sleep' | 'gi' | 'seizures' | 'pain' | 'general' | 'custom';
  customSymptom?: string;
  timeSegment?: 'morning' | 'midday' | 'evening' | 'whole-day';
}

interface Pattern {
  symptom: string;
  frequency: string;
  commonTriggers: string[];
  timeOfDay: string;
  correlation: number;
}

interface SymptomTemplate {
  domain: string;
  icon: string;
  symptoms: string[];
  description: string;
  severityScale?: string;
  specialNotes?: string;
}

interface EpisodicEvent {
  id: string;
  type: 'seizure' | 'meltdown' | 'elopement' | 'self-injury';
  startTime: Date;
  endTime?: Date;
  duration?: number; // in minutes
  severity: number;
  status: 'active' | 'ended';
  context?: string;
}

const SymptomNutrientInsights: React.FC<SymptomNutrientInsightsProps> = ({ 
  onBack, 
  symptomName = 'Fatigue', 
  severity = 3, 
  date = 'Today' 
 }) => {
  const [activeSection, setActiveSection] = useState<'today' | 'quick-log' | 'patterns' | 'actions' | 'history' | 'share'>('today');
  const [showQuickLogModal, setShowQuickLogModal] = useState(false);
  const [showPatternExplorer, setShowPatternExplorer] = useState(false);
  const [selectedSymptom, setSelectedSymptom] = useState<string>('');
  const [selectedDomain, setSelectedDomain] = useState<string>('');
  const [selectedTimeSegment, setSelectedTimeSegment] = useState<'morning' | 'midday' | 'evening' | 'whole-day'>('whole-day');
  const [episodicEvents, setEpisodicEvents] = useState<EpisodicEvent[]>([]);

  // Comprehensive symptom templates by domain
  const symptomTemplates: SymptomTemplate[] = [
    {
      domain: 'Motor',
      icon: 'ðŸƒ',
      symptoms: ['Fatigue', 'Hypotonia impact', 'Falls', 'Gait quality', 'Fine-motor difficulty'],
      description: 'Physical movement, coordination, and motor skills',
      severityScale: '1-5: Normal to Severe impairment',
      specialNotes: 'Monitor for changes in baseline motor function'
    },
    {
      domain: 'Communication/Language',
      icon: 'ðŸ’¬',
      symptoms: ['Vocalizations', 'Gestures/AAC attempts', 'Responsiveness to name', 'New words/signs'],
      description: 'Verbal and non-verbal communication abilities',
      severityScale: '1-5: No issues to Complete communication breakdown',
      specialNotes: 'Track both receptive and expressive language'
    },
    {
      domain: 'Social/Behavior',
      icon: 'ðŸ‘¥',
      symptoms: ['Irritability', 'Self-injury', 'Elopement', 'Meltdown frequency/duration', 'Engagement'],
      description: 'Social interactions and behavioral responses',
      severityScale: '1-5: Typical to Severe behavioral challenges',
      specialNotes: 'Note environmental triggers and context'
    },
    {
      domain: 'Sensory Processing',
      icon: 'ðŸŽ­',
      symptoms: ['Over-responsivity to sound/light/touch', 'Under-responsivity', 'Proprioceptive seeking'],
      description: 'Sensory input processing and responses',
      severityScale: '1-5: Typical to Extreme sensitivity/insensitivity',
      specialNotes: 'Document specific sensory triggers and responses'
    },
    {
      domain: 'Sleep',
      icon: 'ðŸ˜´',
      symptoms: ['Duration', 'Latency', 'Awakenings', 'Restfulness'],
      description: 'Sleep quality and patterns',
      severityScale: '1-5: Excellent to Severe sleep disruption',
      specialNotes: 'Track sleep hygiene and environmental factors'
    },
    {
      domain: 'GI/Feeding',
      icon: 'ðŸ½ï¸',
      symptoms: ['Appetite', 'Reflux', 'Vomiting', 'Abdominal pain', 'Stool (Bristol 1-7)', 'Constipation severity'],
      description: 'Digestive health and feeding behaviors',
      severityScale: '1-5: Normal to Severe GI symptoms',
      specialNotes: 'Monitor Bristol stool scale and feeding patterns'
    },
    {
      domain: 'Seizures/Episodes',
      icon: 'âš¡',
      symptoms: ['Type', 'Duration', 'Cluster', 'Recovery', 'Rescue med used', 'Injuries'],
      description: 'Seizure activity and related episodes',
      severityScale: '1-5: No episodes to Severe/status episodes',
      specialNotes: 'Document rescue medication usage and recovery time'
    },
    {
      domain: 'Pain/Discomfort',
      icon: 'ðŸ˜£',
      symptoms: ['FLACC-based observation', 'Pain insensitivity checks', 'General discomfort'],
      description: 'Pain assessment and discomfort indicators',
      severityScale: 'FLACC 0-10 scale',
      specialNotes: 'Use FLACC scale for nonverbal children; check pain insensitivity (PMS relevant)'
    },
    {
      domain: 'General Health',
      icon: 'ðŸ¥',
      symptoms: ['Fever', 'Rash', 'Cough/wheeze', 'Hydration markers', 'Urine frequency', 'Tears'],
      description: 'Overall health indicators and vital signs',
      severityScale: '1-5: Normal to Severe symptoms',
      specialNotes: 'Monitor hydration and general wellness markers'
    },
    {
      domain: 'Custom',
      icon: 'âœ¨',
      symptoms: ['Add custom symptom'],
      description: 'Caregiver-defined symptoms and observations',
      severityScale: 'Customizable scale',
      specialNotes: 'Can add synonyms that auto-map to canonical items'
    }
  ];

  // Mock data for demonstration - enhanced with time segments and episodic events
  const todaySymptoms: SymptomEntry[] = [
    {
      id: '1',
      symptom: 'Irritability',
      severity: 4,
      duration: '2 hours',
      triggers: ['Loud noises', 'Schedule change'],
      notes: 'Started after morning routine disruption',
      timestamp: new Date(),
      domain: 'social',
      timeSegment: 'morning'
    },
    {
      id: '2',
      symptom: 'Fatigue',
      severity: 3,
      duration: 'All day',
      triggers: ['Poor sleep', 'Overstimulation'],
      notes: 'More tired than usual',
      timestamp: new Date(),
      domain: 'motor',
      timeSegment: 'whole-day'
    },
    {
      id: '3',
      symptom: 'Constipation severity',
      severity: 4,
      duration: '3 days',
      triggers: ['Diet change', 'Dehydration'],
      notes: 'Bristol stool scale 1-2, hard stools',
      timestamp: new Date(),
      domain: 'gi',
      timeSegment: 'whole-day'
    },
    {
      id: '4',
      symptom: 'Sensory sensitivity',
      severity: 3,
      duration: '1 hour',
      triggers: ['Bright lights', 'Crowded environment'],
      notes: 'Covered ears, squinted eyes',
      timestamp: new Date(),
      domain: 'sensory',
      timeSegment: 'midday'
    }
  ];

  // Caregiver-priority symptoms (3-5 most important)
  const prioritySymptoms = ['Irritability', 'Fatigue', 'Constipation severity'];

  const baselineSymptoms = {
    irritability: 2,
    fatigue: 2,
    anxiety: 1,
    focus: 3,
    'constipation severity': 1,
    'sensory sensitivity': 1
  };

  // Mock episodic events
  const mockEpisodicEvents: EpisodicEvent[] = [
    {
      id: '1',
      type: 'meltdown',
      startTime: new Date(Date.now() - 30 * 60 * 1000), // 30 minutes ago
      severity: 4,
      status: 'active'
    }
  ];

  useEffect(() => {
    setEpisodicEvents(mockEpisodicEvents);
  }, []);

  const patterns: Pattern[] = [
    {
      symptom: 'Irritability',
      frequency: '3-4 times/week',
      commonTriggers: ['Loud noises', 'Schedule changes', 'Overstimulation'],
      timeOfDay: 'Morning (9-11 AM)',
      correlation: 0.8
    },
    {
      symptom: 'Fatigue',
      frequency: 'Daily',
      commonTriggers: ['Poor sleep', 'Busy days', 'New environments'],
      timeOfDay: 'Afternoon (2-4 PM)',
      correlation: 0.9
    },
    {
      symptom: 'Constipation severity',
      frequency: '2-3 times/week',
      commonTriggers: ['Diet changes', 'Dehydration', 'Travel'],
      timeOfDay: 'Variable',
      correlation: 0.7
    }
  ];

  const actionCards = [
    {
      title: 'Immediate Calming',
      description: 'Deep breathing, quiet space, favorite toy',
      urgency: 'high',
      category: 'home-strategy'
    },
    {
      title: 'Schedule Adjustment',
      description: 'Consider reducing morning activities',
      urgency: 'medium',
      category: 'prevention'
    },
    {
      title: 'GI Management',
      description: 'Increase hydration, fiber, consider stool softener',
      urgency: 'medium',
      category: 'home-strategy'
    },
    {
      title: 'Contact Provider',
      description: 'If symptoms persist >24 hours',
      urgency: 'low',
      category: 'escalation'
    }
  ];

  const getSeverityColor = (severity: number) => {
    if (severity <= 2) return 'text-green-600 bg-green-100';
    if (severity <= 3) return 'text-yellow-600 bg-yellow-100';
    return 'text-red-600 bg-red-100';
  };

  const getSeverityText = (severity: number) => {
    if (severity <= 2) return 'Mild';
    if (severity <= 3) return 'Moderate';
    return 'Severe';
  };

  const getDomainIcon = (domain: string) => {
    const template = symptomTemplates.find(t => t.domain === domain);
    return template ? template.icon : 'ðŸ“Š';
  };

  const getUrgencyColor = (urgency: string) => {
    const colors = {
      high: 'border-red-200 bg-red-50 text-red-800',
      medium: 'border-yellow-200 bg-yellow-50 text-yellow-800',
      low: 'border-green-200 bg-green-50 text-green-800'
    };
    return colors[urgency as keyof typeof colors] || 'border-gray-200 bg-gray-50';
  };

  const getDomainColor = (domain: string) => {
    const colors = {
      motor: 'bg-blue-50 border-blue-200',
      communication: 'bg-green-50 border-green-200',
      social: 'bg-purple-50 border-purple-200',
      sensory: 'bg-yellow-50 border-yellow-200',
      sleep: 'bg-indigo-50 border-indigo-200',
      gi: 'bg-orange-50 border-orange-200',
      seizures: 'bg-red-50 border-red-200',
      pain: 'bg-pink-50 border-pink-200',
      general: 'bg-gray-50 border-gray-200',
      custom: 'bg-teal-50 border-teal-200'
    };
    return colors[domain as keyof typeof colors] || 'bg-gray-50 border-gray-200';
  };

  const getStatusStatement = (symptom: SymptomEntry) => {
    const baseline = baselineSymptoms[symptom.symptom.toLowerCase() as keyof typeof baselineSymptoms] || 2;
    const change = symptom.severity - baseline;
    
    if (change > 0) {
      return `${symptom.symptom} is higher than usual (${symptom.severity} of 5; typical ${baseline}-${Math.min(baseline + 1, 5)})`;
    } else if (change < 0) {
      return `${symptom.symptom} is lower than usual (${symptom.severity} of 5; typical ${Math.max(baseline - 1, 1)}-${baseline})`;
    } else {
      return `${symptom.symptom} is at typical level (${symptom.severity} of 5)`;
    }
  };

  const getBaselineDelta = (symptom: SymptomEntry) => {
    const baseline = baselineSymptoms[symptom.symptom.toLowerCase() as keyof typeof baselineSymptoms] || 2;
    const change = symptom.severity - baseline;
    const percentChange = Math.round((change / baseline) * 100);
    
    if (change > 0) {
      return `+${change} (+${percentChange}%)`;
    } else if (change < 0) {
      return `${change} (${percentChange}%)`;
    } else {
      return '0 (0%)';
    }
  };

  const getBaselineDeltaColor = (symptom: SymptomEntry) => {
    const baseline = baselineSymptoms[symptom.symptom.toLowerCase() as keyof typeof baselineSymptoms] || 2;
    const change = symptom.severity - baseline;
    
    if (change > 1) return 'text-red-600';
    if (change > 0) return 'text-orange-600';
    if (change < 0) return 'text-green-600';
    return 'text-gray-600';
  };

  const startEpisodicEvent = (type: EpisodicEvent['type']) => {
    const newEvent: EpisodicEvent = {
      id: Date.now().toString(),
      type,
      startTime: new Date(),
      severity: 3,
      status: 'active'
    };
    setEpisodicEvents(prev => [...prev, newEvent]);
  };

  const stopEpisodicEvent = (eventId: string) => {
    setEpisodicEvents(prev => prev.map(event => 
      event.id === eventId 
        ? { ...event, status: 'ended', endTime: new Date() }
        : event
    ));
  };

  const getEventDuration = (event: EpisodicEvent) => {
    if (event.status === 'ended' && event.endTime) {
      return Math.round((event.endTime.getTime() - event.startTime.getTime()) / (1000 * 60));
    }
    return Math.round((Date.now() - event.startTime.getTime()) / (1000 * 60));
  };

  const getEventIcon = (type: string) => {
    const icons = {
      seizure: 'âš¡',
      meltdown: 'ðŸ˜¤',
      elopement: 'ðŸƒ',
      'self-injury': 'âš ï¸'
    };
    return icons[type as keyof typeof icons] || 'ðŸ“Š';
  };

  const getEventColor = (type: string) => {
    const colors = {
      seizure: 'bg-red-100 border-red-300',
      meltdown: 'bg-orange-100 border-orange-300',
      elopement: 'bg-yellow-100 border-yellow-300',
      'self-injury': 'bg-red-100 border-red-300'
    };
    return colors[type as keyof typeof colors] || 'bg-gray-100 border-gray-300';
  };

  const filteredSymptoms = selectedTimeSegment === 'whole-day' 
    ? todaySymptoms 
    : todaySymptoms.filter(s => s.timeSegment === selectedTimeSegment || s.timeSegment === 'whole-day');

  const priorityFilteredSymptoms = filteredSymptoms.filter(s => 
    prioritySymptoms.includes(s.symptom)
  );

  return (
    <div className="min-h-screen bg-gray-50 pb-20">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200 px-4 py-3 lg:px-8 lg:py-4">
        <div className="flex items-center space-x-3">
          <button
            onClick={onBack}
            className="p-1 text-gray-400 hover:text-gray-600 transition-colors"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>
          <div className="flex items-center space-x-2">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
            </svg>
            <h1 className="text-lg font-semibold text-gray-900 lg:text-xl">Symptom Tracker</h1>
          </div>
        </div>
      </header>

      {/* Navigation Tabs */}
      <nav className="bg-white border-b border-gray-200 px-4 lg:px-8">
        <div className="flex space-x-8 overflow-x-auto">
          {[
            { id: 'today', label: 'Today Lens', icon: 'ðŸ“…' },
            { id: 'quick-log', label: 'Quick Log', icon: 'âš¡' },
            { id: 'patterns', label: 'Patterns', icon: 'ðŸ”' },
            { id: 'actions', label: 'Actions', icon: 'ðŸŽ¯' },
            { id: 'history', label: 'History', icon: 'ðŸ“Š' },
            { id: 'share', label: 'Share', icon: 'ðŸ“¤' }
          ].map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveSection(tab.id as any)}
              className={`flex items-center space-x-2 py-3 px-1 border-b-2 whitespace-nowrap transition-colors ${
                activeSection === tab.id
                  ? 'border-indigo-500 text-indigo-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700'
              }`}
            >
              <span className="text-sm">{tab.icon}</span>
              <span className="text-sm font-medium lg:text-base">{tab.label}</span>
            </button>
          ))}
        </div>
      </nav>

      {/* Main Content */}
      <main className="px-4 py-6 lg:px-8 lg:py-8 lg:max-w-7xl lg:mx-auto">
        {/* Today Lens Section */}
        {activeSection === 'today' && (
          <div className="space-y-6">
            {/* Today's Overview */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 lg:text-2xl">Today's Overview</h2>
              
              {/* One-tap Timeline Toggle */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-3">Time Segment</label>
                <div className="flex space-x-2">
                  {[
                    { id: 'morning', label: 'Morning', icon: 'ðŸŒ…' },
                    { id: 'midday', label: 'Midday', icon: 'â˜€ï¸' },
                    { id: 'evening', label: 'Evening', icon: 'ðŸŒ†' },
                    { id: 'whole-day', label: 'Whole Day', icon: 'ðŸ“…' }
                  ].map((segment) => (
                    <button
                      key={segment.id}
                      onClick={() => setSelectedTimeSegment(segment.id as any)}
                      className={`flex items-center space-x-2 px-4 py-2 rounded-lg border-2 transition-all ${
                        selectedTimeSegment === segment.id
                          ? 'border-indigo-500 bg-indigo-50 text-indigo-700'
                          : 'border-gray-200 hover:border-gray-300 text-gray-600'
                      }`}
                    >
                      <span className="text-sm">{segment.icon}</span>
                      <span className="text-sm font-medium">{segment.label}</span>
                    </button>
                  ))}
                </div>
              </div>

              {/* Quick Stats */}
              <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
                <div className="text-center p-4 bg-blue-50 rounded-lg">
                  <div className="text-2xl font-bold text-blue-600">{filteredSymptoms.length}</div>
                  <div className="text-sm text-blue-600">Symptoms {selectedTimeSegment === 'whole-day' ? 'Today' : selectedTimeSegment}</div>
                </div>
                <div className="text-center p-4 bg-yellow-50 rounded-lg">
                  <div className="text-2xl font-bold text-yellow-600">
                    {filteredSymptoms.length > 0 ? Math.round(filteredSymptoms.reduce((acc, s) => acc + s.severity, 0) / filteredSymptoms.length) : 0}
                  </div>
                  <div className="text-sm text-yellow-600">Avg Severity</div>
                </div>
                <div className="text-center p-4 bg-green-50 rounded-lg">
                  <div className="text-2xl font-bold text-green-600">
                    {filteredSymptoms.filter(s => s.severity <= 2).length}
                  </div>
                  <div className="text-sm text-green-600">Mild Symptoms</div>
                </div>
                <div className="text-center p-4 bg-red-50 rounded-lg">
                  <div className="text-2xl font-bold text-red-600">
                    {filteredSymptoms.filter(s => s.severity >= 4).length}
                  </div>
                  <div className="text-sm text-red-600">Severe Symptoms</div>
                </div>
              </div>

              {/* Now vs Baseline Meter - Caregiver Priority Symptoms */}
              <div className="mb-6">
                <h3 className="text-lg font-medium text-gray-900 mb-3">Now vs. Baseline - Priority Symptoms</h3>
                <div className="space-y-3">
                  {priorityFilteredSymptoms.map((symptom) => (
                    <div key={symptom.id} className={`flex items-center justify-between p-4 rounded-lg border ${getDomainColor(symptom.domain)}`}>
                      <div className="flex items-center space-x-3">
                        <span className="text-2xl">{getDomainIcon(symptom.domain)}</span>
                        <div>
                          <div className="font-medium text-gray-900">{symptom.symptom}</div>
                          <div className="text-sm text-gray-500">{symptom.duration}</div>
                        </div>
                      </div>
                      <div className="flex items-center space-x-4">
                        <div className={`px-3 py-1 rounded-full text-sm font-medium ${getSeverityColor(symptom.severity)}`}>
                          {getSeverityText(symptom.severity)} ({symptom.severity}/5)
                        </div>
                        <div className={`text-sm font-medium ${getBaselineDeltaColor(symptom)}`}>
                          {getBaselineDelta(symptom)}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Status Statements in Plain Language */}
              <div className="mb-6">
                <h3 className="text-lg font-medium text-gray-900 mb-3">Status Summary</h3>
                <div className="space-y-2">
                  {priorityFilteredSymptoms.map((symptom) => (
                    <div key={symptom.id} className="p-3 bg-gray-50 rounded-lg">
                      <p className="text-sm text-gray-700">{getStatusStatement(symptom)}</p>
                    </div>
                  ))}
                </div>
              </div>

              {/* Event Ticker - Ongoing Episodic Events */}
              <div className="mb-6">
                <h3 className="text-lg font-medium text-gray-900 mb-3">Active Events</h3>
                <div className="space-y-3">
                  {episodicEvents.filter(e => e.status === 'active').map((event) => (
                    <div key={event.id} className={`p-4 rounded-lg border-2 ${getEventColor(event.type)}`}>
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-3">
                          <span className="text-2xl">{getEventIcon(event.type)}</span>
                          <div>
                            <div className="font-medium text-gray-900 capitalize">{event.type}</div>
                            <div className="text-sm text-gray-600">
                              Duration: {getEventDuration(event)} minutes
                            </div>
                          </div>
                        </div>
                        <button
                          onClick={() => stopEpisodicEvent(event.id)}
                          className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg font-medium transition-colors"
                        >
                          Stop Timer
                        </button>
                      </div>
                    </div>
                  ))}
                  
                  {/* Quick Start Buttons */}
                  <div className="flex flex-wrap gap-2">
                    {['seizure', 'meltdown', 'elopement', 'self-injury'].map((type) => (
                      <button
                        key={type}
                        onClick={() => startEpisodicEvent(type as any)}
                        className="px-3 py-2 bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-lg text-sm font-medium transition-colors"
                      >
                        Start {type.charAt(0).toUpperCase() + type.slice(1)} Timer
                      </button>
                    ))}
                  </div>
                </div>
              </div>

              {/* Quick Actions */}
              <div className="flex flex-col sm:flex-row gap-3">
                <button
                  onClick={() => setActiveSection('quick-log')}
                  className="flex-1 bg-indigo-600 hover:bg-indigo-700 text-white py-3 px-4 rounded-lg font-medium transition-colors"
                >
                  Log New Symptom
                </button>
                <button
                  onClick={() => setActiveSection('patterns')}
                  className="flex-1 bg-gray-100 hover:bg-gray-200 text-gray-700 py-3 px-4 rounded-lg font-medium transition-colors"
                >
                  View Patterns
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Quick Log Section */}
        {activeSection === 'quick-log' && (
          <div className="space-y-6">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 lg:text-2xl">Quick Symptom Log</h2>
              <p className="text-gray-600 mb-6">Record symptoms in 10-20 seconds</p>
              
              {/* Domain Selection */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-3">Select Domain</label>
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-3">
                  {symptomTemplates.map((template) => (
                    <button
                      key={template.domain}
                      onClick={() => setSelectedDomain(template.domain)}
                      className={`p-3 rounded-lg border-2 text-center transition-all ${
                        selectedDomain === template.domain
                          ? 'border-indigo-500 bg-indigo-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }`}
                    >
                      <div className="text-2xl mb-1">{template.icon}</div>
                      <div className="text-xs font-medium text-gray-700">{template.domain}</div>
                    </button>
                  ))}
                </div>
              </div>

              {/* Symptom Selection */}
              {selectedDomain && (
                <div className="mb-6">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Select Symptom</label>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                    {symptomTemplates
                      .find(t => t.domain === selectedDomain)
                      ?.symptoms.map((symptom) => (
                        <button
                          key={symptom}
                          onClick={() => setSelectedSymptom(symptom)}
                          className={`p-3 rounded-lg border text-left transition-all ${
                            selectedSymptom === symptom
                              ? 'border-indigo-500 bg-indigo-50'
                              : 'border-gray-200 hover:border-gray-300'
                          }`}
                        >
                          <div className="font-medium text-gray-900">{symptom}</div>
                          {symptomTemplates.find(t => t.domain === selectedDomain)?.specialNotes && (
                            <div className="text-xs text-gray-500 mt-1">
                              {symptomTemplates.find(t => t.domain === selectedDomain)?.specialNotes}
                            </div>
                          )}
                        </button>
                      ))}
                  </div>
                </div>
              )}

              {/* Quick Log Form */}
              {selectedSymptom && (
                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Severity {symptomTemplates.find(t => t.domain === selectedDomain)?.severityScale || '(1-5)'}
                      </label>
                      <div className="flex space-x-2">
                        {[1, 2, 3, 4, 5].map((level) => (
                          <button
                            key={level}
                            className="w-10 h-10 rounded-full border-2 border-gray-300 hover:border-indigo-500 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                          >
                            {level}
                          </button>
                        ))}
                      </div>
                    </div>
                    
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Duration</label>
                      <select className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500">
                        <option>Select duration...</option>
                        <option>Few minutes</option>
                        <option>1-2 hours</option>
                        <option>Half day</option>
                        <option>All day</option>
                        <option>Multiple days</option>
                      </select>
                    </div>
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Triggers (optional)</label>
                    <input
                      type="text"
                      placeholder="e.g., Loud noises, schedule change, food..."
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                    />
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Notes (optional)</label>
                    <textarea
                      rows={3}
                      placeholder="Any additional context..."
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                    />
                  </div>
                  
                  <button className="w-full bg-indigo-600 hover:bg-indigo-700 text-white py-3 px-4 rounded-lg font-medium transition-colors">
                    Save Symptom Log
                  </button>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Patterns & Triggers Section */}
        {activeSection === 'patterns' && (
          <div className="space-y-6">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 lg:text-2xl">Patterns & Triggers</h2>
              <p className="text-gray-600 mb-6">Simple insights to deeper exploration</p>
              
              {/* Pattern Cards */}
              <div className="space-y-4">
                {patterns.map((pattern, index) => (
                  <div key={index} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                    <div className="flex items-start justify-between mb-3">
                      <h3 className="text-lg font-medium text-gray-900">{pattern.symptom}</h3>
                      <div className="text-sm text-gray-500">Correlation: {pattern.correlation * 100}%</div>
                    </div>
                    
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                      <div>
                        <span className="font-medium text-gray-700">Frequency:</span> {pattern.frequency}
                      </div>
                      <div>
                        <span className="font-medium text-gray-700">Time of day:</span> {pattern.timeOfDay}
                      </div>
                    </div>
                    
                    <div className="mt-3">
                      <span className="font-medium text-gray-700">Common triggers:</span>
                      <div className="flex flex-wrap gap-2 mt-1">
                        {pattern.commonTriggers.map((trigger, idx) => (
                          <span key={idx} className="px-2 py-1 bg-gray-100 text-gray-700 text-xs rounded-full">
                            {trigger}
                          </span>
                        ))}
                      </div>
                    </div>
                    
                    <button
                      onClick={() => setShowPatternExplorer(true)}
                      className="mt-3 text-indigo-600 hover:text-indigo-700 text-sm font-medium"
                    >
                      Explore deeper â†’
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Action Cards Section */}
        {activeSection === 'actions' && (
          <div className="space-y-6">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 lg:text-2xl">Action Cards</h2>
              <p className="text-gray-600 mb-6">Home strategies and escalation guidance</p>
              
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {actionCards.map((action, index) => (
                  <div key={index} className={`border-2 rounded-lg p-4 ${getUrgencyColor(action.urgency)}`}>
                    <div className="flex items-start justify-between mb-2">
                      <h3 className="font-medium text-gray-900">{action.title}</h3>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                        action.urgency === 'high' ? 'bg-red-100 text-red-800' :
                        action.urgency === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-green-100 text-green-800'
                      }`}>
                        {action.urgency} priority
                      </span>
                    </div>
                    <p className="text-sm text-gray-600 mb-3">{action.description}</p>
                    <div className="text-xs text-gray-500 capitalize">{action.category}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* History & Visualizations Section */}
        {activeSection === 'history' && (
          <div className="space-y-6">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 lg:text-2xl">History & Visualizations</h2>
              <p className="text-gray-600 mb-6">7/30/90-day symptom trends</p>
              
              {/* Time Period Selector */}
              <div className="flex space-x-2 mb-6">
                {['7 days', '30 days', '90 days'].map((period) => (
                  <button
                    key={period}
                    className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  >
                    {period}
                  </button>
                ))}
              </div>
              
              {/* Placeholder for Charts */}
              <div className="h-64 bg-gray-100 rounded-lg flex items-center justify-center">
                <div className="text-center">
                  <div className="text-4xl mb-2">ðŸ“Š</div>
                  <p className="text-gray-600">Symptom trend charts will appear here</p>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Share & Feedback Section */}
        {activeSection === 'share' && (
          <div className="space-y-6">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4 lg:text-2xl">Share & Feedback</h2>
              <p className="text-gray-600 mb-6">For clinicians, teachers, and model learning</p>
              
              <div className="space-y-4">
                <div className="border border-gray-200 rounded-lg p-4">
                  <h3 className="font-medium text-gray-900 mb-2">Generate Report</h3>
                  <p className="text-sm text-gray-600 mb-3">Create a comprehensive symptom summary for healthcare providers</p>
                  <button className="bg-indigo-600 hover:bg-indigo-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition-colors">
                    Generate Report
                  </button>
                </div>
                
                <div className="border border-gray-200 rounded-lg p-4">
                  <h3 className="font-medium text-gray-900 mb-2">Share with Team</h3>
                  <p className="text-sm text-gray-600 mb-3">Send updates to teachers, therapists, or family members</p>
                  <button className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition-colors">
                    Share Update
                  </button>
                </div>
                
                <div className="border border-gray-200 rounded-lg p-4">
                  <h3 className="font-medium text-gray-900 mb-2">Feedback & Learning</h3>
                  <p className="text-sm text-gray-600 mb-3">Help improve the symptom tracking system</p>
                  <button className="bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg text-sm font-medium transition-colors">
                    Provide Feedback
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default SymptomNutrientInsights;
