sourcesContent:
import React, { useState } from 'react';

interface GrowthData {
  date: string;
  height: number;
  weight: number;
  notes: string;
}

interface DevelopmentMilestone {
  id: string;
  title: string;
  description: string;
  achieved: boolean;
  date?: string;
}

interface ChildProfile {
  name: string;
  age: string;
  condition: string;
  lastUpdated: string;
}

interface ProgressData {
  typicalProgress: number;
  pmsProgress: number;
  currentAge: number;
  targetAge: number;
}

interface MilestoneBadge {
  id: string;
  title: string;
  date: string;
  icon: string;
  category: 'physical' | 'cognitive' | 'social' | 'emotional';
}

interface DomainData {
  id: string;
  name: string;
  icon: string;
  strengths: string[];
  challenges: string[];
  progressData: {
    typical: number;
    pms: number;
    timeline: { date: string; typical: number; pms: number }[];
  };
  nextMilestones: {
    title: string;
    description: string;
    timeline: string;
    tips: string[];
  }[];
}

interface SurveyQuestion {
  id: string;
  question: string;
  type: 'multiple-choice' | 'scale' | 'text';
  options?: string[];
  domain: string;
}

interface Recommendation {
  id: string;
  title: string;
  description: string;
  category: 'therapy' | 'intervention' | 'assessment' | 'strategy';
  priority: 'high' | 'medium' | 'low';
  evidence: string;
  actionUrl?: string;
}

interface NextStep {
  id: string;
  title: string;
  description: string;
  dueDate: string;
  status: 'pending' | 'scheduled' | 'completed';
  type: 'assessment' | 'screening' | 'appointment' | 'follow-up';
}

interface ContentItem {
  id: string;
  title: string;
  type: 'video' | 'infographic' | 'article';
  duration?: string;
  description: string;
  url: string;
}

interface Goal {
  id: string;
  title: string;
  description: string;
  category: 'motor' | 'language' | 'social' | 'cognitive' | 'adaptive';
  targetDate: string;
  progress: number;
  status: 'active' | 'achieved' | 'paused';
  reminders: boolean;
  aiSuggested: boolean;
  createdAt: string;
}

interface AISuggestion {
  id: string;
  title: string;
  description: string;
  category: string;
  reason: string;
}

interface CommunityBenchmark {
  metric: string;
  average: number;
  range: string;
  sampleSize: number;
}

interface ClinicalReviewer {
  name: string;
  credentials: string;
  specialty: string;
  institution: string;
}

// AI/API Integration Interfaces
interface SurveyResponse {
  questionId: string;
  response: string | number;
  timestamp: Date;
}

interface MilestoneScore {
  domain: string;
  score: number;
  percentile: number;
  flagged: boolean;
  confidence: number;
}

interface NLPAnalysis {
  sentiment: 'positive' | 'neutral' | 'negative';
  keywords: string[];
  flags: string[];
  suggestions: string[];
  confidence: number;
}

interface MediaAnalysis {
  type: 'video' | 'audio';
  duration: number;
  developmentalCues: string[];
  milestoneVerification: {
    milestone: string;
    confidence: number;
    verified: boolean;
  }[];
  feedback: string;
}

interface AIRecommendation {
  id: string;
  type: 'intervention' | 'assessment' | 'goal' | 'resource';
  title: string;
  description: string;
  priority: 'high' | 'medium' | 'low';
  confidence: number;
  reasoning: string;
  evidence: string[];
}

interface ExportData {
  format: 'pdf' | 'csv';
  sections: string[];
  dateRange: {
    start: Date;
    end: Date;
  };
  includeMedia: boolean;
}

interface ClinicianInvite {
  email: string;
  permissions: string[];
  expiryDate: Date;
  secureLink: string;
}

interface GrowthDevelopmentScreenProps {
  onBack?: () => void;
  onNavigateToNutrition?: () => void;
  onNavigateToSymptomLogs?: () => void;
}

const GrowthDevelopmentScreen: React.FC<GrowthDevelopmentScreenProps> = ({ 
  onBack,
  onNavigateToNutrition,
  onNavigateToSymptomLogs
}) => {
  const [activeTab, setActiveTab] = useState('motor');
  const [isObservationPanelExpanded, setIsObservationPanelExpanded] = useState(false);
  const [showSurvey, setShowSurvey] = useState(false);
  const [showContentLibrary, setShowContentLibrary] = useState(false);
