import React, { useState, useEffect } from 'react';

// Icon components
const SymptomIcon = () => (
  <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
  </svg>
);

const NutritionIcon = () => (
  <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4m0 0L7 13m0 0l-2.5 5M7 13l2.5 5m6-5v6a2 2 0 01-2 2H9a2 2 0 01-2-2v-6m8 0V9a2 2 0 00-2-2H9a2 2 0 00-2 2v4.01" />
  </svg>
);

const MedicationIcon = () => (
  <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19.428 15.428a2 2 0 00-1.022-.547l-2.387-.477a6 6 0 00-3.86.517l-.318.158a6 6 0 01-3.86.517L6.05 15.21a2 2 0 00-1.806.547M8 4h8l-1 1v5.172a2 2 0 00.586 1.414l5 5c1.26 1.26.367 3.414-1.415 3.414H4.828c-1.782 0-2.674-2.154-1.414-3.414l5-5A2 2 0 009 10.172V5L8 4z" />
  </svg>
);

const AIIcon = () => (
  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
  </svg>
);

const CameraIcon = () => (
  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z" />
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 13a3 3 0 11-6 0 3 3 0 016 0z" />
  </svg>
);

const MicrophoneIcon = () => (
  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" />
  </svg>
);

interface QuickLogProps {
  onSave: (data: any) => void;
  onCancel: () => void;
}

const QuickLog: React.FC<QuickLogProps> = ({ onSave, onCancel }) => {
  // State for log type selection - remember last choice
  const [selectedLogType, setSelectedLogType] = useState<'symptom' | 'nutrition' | 'medication'>('symptom');
  const [showAIModal, setShowAIModal] = useState(false);
  const [showSavedToast, setShowSavedToast] = useState(false);
  
  // Form states
  const [symptomForm, setSymptomForm] = useState({
    symptom: '',
    severity: 3,
    notes: '',
    startDate: new Date().toISOString().split('T')[0]
  });

  // Detailed symptom form states
  const [symptomStep, setSymptomStep] = useState<1 | 2 | 3>(1);
  const [selectedSymptomType, setSelectedSymptomType] = useState<string>('');
  const [pinnedSymptomTypes, setPinnedSymptomTypes] = useState<string[]>([]);
  const [showManageModal, setShowManageModal] = useState(false);
  const [showEmergencyModal, setShowEmergencyModal] = useState(false);
  
  // Core fields
  const [coreFields, setCoreFields] = useState({
    startTime: new Date().toISOString().slice(0, 16), // Now as default
    duration: '',
    isOngoing: false,
    severity: 3,
    contextTags: [] as string[],
    notes: ''
  });

  // Type-specific fields
  const [typeSpecificFields, setTypeSpecificFields] = useState<any>({});
  
  const [nutritionForm, setNutritionForm] = useState({
    mealType: '',
    foodItems: '',
    notes: '',
    date: new Date().toISOString().split('T')[0]
  });
  
  const [medicationForm, setMedicationForm] = useState({
    medication: '',
    dosage: '',
    taken: true,
    notes: '',
    date: new Date().toISOString().split('T')[0]
  });

  // Validation states
  const [errors, setErrors] = useState<{[key: string]: string}>({});

  // Load last selection from localStorage
  useEffect(() => {
    const savedLogType = localStorage.getItem('quickLogType');
    if (savedLogType === 'symptom' || savedLogType === 'nutrition' || savedLogType === 'medication') {
      setSelectedLogType(savedLogType);
    }
    
    // Load pinned symptom types
    const savedPinned = localStorage.getItem('pinnedSymptomTypes');
    if (savedPinned) {
      setPinnedSymptomTypes(JSON.parse(savedPinned));
    }
  }, []);

  // Save selection to localStorage
  const handleLogTypeChange = (logType: 'symptom' | 'nutrition' | 'medication') => {
    setSelectedLogType(logType);
    localStorage.setItem('quickLogType', logType);
    setErrors({}); // Clear errors when switching
    
    // Reset symptom form when switching to symptom
    if (logType === 'symptom') {
      setSymptomStep(1);
      setSelectedSymptomType('');
      setCoreFields({
        startTime: new Date().toISOString().slice(0, 16),
        duration: '',
        isOngoing: false,
        severity: 3,
        contextTags: [],
        notes: ''
      });
      setTypeSpecificFields({});
    }
  };

  // Symptom type management
  const handlePinSymptomType = (type: string) => {
    const newPinned = pinnedSymptomTypes.includes(type)
      ? pinnedSymptomTypes.filter(t => t !== type)
      : [...pinnedSymptomTypes, type].slice(0, 5); // Keep only top 5
    setPinnedSymptomTypes(newPinned);
    localStorage.setItem('pinnedSymptomTypes', JSON.stringify(newPinned));
  };

  const handleSymptomTypeSelect = (type: string) => {
    setSelectedSymptomType(type);
    setSymptomStep(2);
  };

  const handleNextStep = () => {
    if (symptomStep < 3) {
      setSymptomStep(symptomStep + 1 as 1 | 2 | 3);
    }
  };

  const handlePrevStep = () => {
    if (symptomStep > 1) {
      setSymptomStep(symptomStep - 1 as 1 | 2 | 3);
    }
  };

  // Validation functions
  const validateSymptomForm = () => {
    const newErrors: {[key: string]: string} = {};
    if (!symptomForm.symptom.trim()) {
      newErrors.symptom = 'Symptom is required';
    }
    return newErrors;
  };

  const validateNutritionForm = () => {
    const newErrors: {[key: string]: string} = {};
    if (!nutritionForm.mealType.trim()) {
      newErrors.mealType = 'Meal type is required';
    }
    if (!nutritionForm.foodItems.trim()) {
      newErrors.foodItems = 'Food items are required';
    }
    return newErrors;
  };

  const validateMedicationForm = () => {
    const newErrors: {[key: string]: string} = {};
    if (!medicationForm.medication.trim()) {
      newErrors.medication = 'Medication name is required';
    }
    if (!medicationForm.dosage.trim()) {
      newErrors.dosage = 'Dosage is required';
    }
    return newErrors;
  };

  // Save functions
  const handleSave = () => {
    let newErrors = {};
    
    switch (selectedLogType) {
      case 'symptom':
        newErrors = validateSymptomForm();
        if (Object.keys(newErrors).length === 0) {
          onSave({ type: 'symptom', data: symptomForm });
          setShowSavedToast(true);
          setTimeout(() => setShowSavedToast(false), 3000);
          // Reset form
          setSymptomForm({
            symptom: '',
            severity: 3,
            notes: '',
            startDate: new Date().toISOString().split('T')[0]
          });
        }
        break;
      case 'nutrition':
        newErrors = validateNutritionForm();
        if (Object.keys(newErrors).length === 0) {
          onSave({ type: 'nutrition', data: nutritionForm });
          setShowSavedToast(true);
          setTimeout(() => setShowSavedToast(false), 3000);
          // Reset form
          setNutritionForm({
            mealType: '',
            foodItems: '',
            notes: '',
            date: new Date().toISOString().split('T')[0]
          });
        }
        break;
      case 'medication':
        newErrors = validateMedicationForm();
        if (Object.keys(newErrors).length === 0) {
          onSave({ type: 'medication', data: medicationForm });
          setShowSavedToast(true);
          setTimeout(() => setShowSavedToast(false), 3000);
          // Reset form
          setMedicationForm({
            medication: '',
            dosage: '',
            taken: true,
            notes: '',
            date: new Date().toISOString().split('T')[0]
          });
        }
        break;
    }
    
    setErrors(newErrors);
  };

  const handleSaveAndAddAnother = () => {
    handleSave();
    // Form will be reset after save, so we can stay on the same form
  };

  // Symptom type data
  const symptomTypes = [
    { id: 'seizure', name: 'Seizure', icon: '⚡', color: 'bg-purple-500' },
    { id: 'sleep', name: 'Sleep', icon: '😴', color: 'bg-blue-500' },
    { id: 'gi', name: 'GI', icon: '🤢', color: 'bg-orange-500' },
    { id: 'pain', name: 'Pain/Discomfort', icon: '😣', color: 'bg-red-500' },
    { id: 'sensory', name: 'Sensory/Autonomic', icon: '🌡️', color: 'bg-yellow-500' },
    { id: 'respiratory', name: 'Respiratory/ENT', icon: '🫁', color: 'bg-cyan-500' },
    { id: 'behavior', name: 'Behavior Episode', icon: '😤', color: 'bg-pink-500' },
    { id: 'motor', name: 'Motor', icon: '🏃', color: 'bg-green-500' }
  ];

  // Context tags
  const contextTags = [
    'therapy day', 'illness', 'travel', 'loud environment', 'school day', 'menses'
  ];

  // Log type tiles
  const logTypes = [
    {
      id: 'symptom',
      title: 'Symptom',
      icon: <SymptomIcon />,
      color: 'bg-gradient-to-br from-red-500 to-pink-500',
      description: 'Log symptoms and severity'
    },
    {
      id: 'nutrition',
      title: 'Nutrition',
      icon: <NutritionIcon />,
      color: 'bg-gradient-to-br from-green-500 to-emerald-500',
      description: 'Track meals and food intake'
    },
    {
      id: 'medication',
      title: 'Medication',
      icon: <MedicationIcon />,
      color: 'bg-gradient-to-br from-blue-500 to-indigo-500',
      description: 'Record medication taken'
    }
  ];

  // Render form based on selected type
  const renderForm = () => {
    switch (selectedLogType) {
      case 'symptom':
        return (
          <div className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Symptom <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                value={symptomForm.symptom}
                onChange={(e) => setSymptomForm({...symptomForm, symptom: e.target.value})}
                className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 ${
                  errors.symptom ? 'border-red-300' : 'border-gray-300'
                }`}
                placeholder="e.g., Headache, Fatigue, Nausea"
              />
              {errors.symptom && (
                <p className="mt-1 text-sm text-red-600">{errors.symptom}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Severity
              </label>
              <div className="flex space-x-4">
                {[1, 2, 3, 4, 5].map((level) => (
                  <button
                    key={level}
                    onClick={() => setSymptomForm({...symptomForm, severity: level})}
                    className={`flex-1 py-3 px-4 rounded-lg border-2 transition-colors ${
                      symptomForm.severity === level
                        ? 'border-indigo-500 bg-indigo-50 text-indigo-700'
                        : 'border-gray-300 text-gray-700 hover:border-gray-400'
                    }`}
                  >
                    <div className="text-center">
                      <div className="text-lg font-semibold">{level}</div>
                      <div className="text-xs">
                        {level === 1 ? 'Very Mild' : 
                         level === 2 ? 'Mild' : 
                         level === 3 ? 'Moderate' : 
                         level === 4 ? 'Severe' : 'Very Severe'}
                      </div>
                    </div>
                  </button>
                ))}
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Notes (optional)
              </label>
              <textarea
                value={symptomForm.notes}
                onChange={(e) => setSymptomForm({...symptomForm, notes: e.target.value})}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                placeholder="Describe what you're experiencing..."
                rows={3}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                When did this start?
              </label>
              <input
                type="date"
                value={symptomForm.startDate}
                onChange={(e) => setSymptomForm({...symptomForm, startDate: e.target.value})}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
              />
            </div>
          </div>
        );

      case 'nutrition':
        return (
          <div className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Meal Type <span className="text-red-500">*</span>
              </label>
              <select
                value={nutritionForm.mealType}
                onChange={(e) => setNutritionForm({...nutritionForm, mealType: e.target.value})}
                className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 ${
                  errors.mealType ? 'border-red-300' : 'border-gray-300'
                }`}
              >
                <option value="">Select meal type</option>
                <option value="breakfast">Breakfast</option>
                <option value="lunch">Lunch</option>
                <option value="dinner">Dinner</option>
                <option value="snack">Snack</option>
              </select>
              {errors.mealType && (
                <p className="mt-1 text-sm text-red-600">{errors.mealType}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Food Items <span className="text-red-500">*</span>
              </label>
              <textarea
                value={nutritionForm.foodItems}
                onChange={(e) => setNutritionForm({...nutritionForm, foodItems: e.target.value})}
                className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 ${
                  errors.foodItems ? 'border-red-300' : 'border-gray-300'
                }`}
                placeholder="List the foods you ate..."
                rows={3}
              />
              {errors.foodItems && (
                <p className="mt-1 text-sm text-red-600">{errors.foodItems}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Notes (optional)
              </label>
              <textarea
                value={nutritionForm.notes}
                onChange={(e) => setNutritionForm({...nutritionForm, notes: e.target.value})}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                placeholder="Any observations or reactions..."
                rows={2}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Date
              </label>
              <input
                type="date"
                value={nutritionForm.date}
                onChange={(e) => setNutritionForm({...nutritionForm, date: e.target.value})}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
              />
            </div>
          </div>
        );

      case 'medication':
        return (
          <div className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Medication Name <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                value={medicationForm.medication}
                onChange={(e) => setMedicationForm({...medicationForm, medication: e.target.value})}
                className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 ${
                  errors.medication ? 'border-red-300' : 'border-gray-300'
                }`}
                placeholder="e.g., Tylenol, Aspirin"
              />
              {errors.medication && (
                <p className="mt-1 text-sm text-red-600">{errors.medication}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Dosage <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                value={medicationForm.dosage}
                onChange={(e) => setMedicationForm({...medicationForm, dosage: e.target.value})}
                className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 ${
                  errors.dosage ? 'border-red-300' : 'border-gray-300'
                }`}
                placeholder="e.g., 500mg, 1 tablet"
              />
              {errors.dosage && (
                <p className="mt-1 text-sm text-red-600">{errors.dosage}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Taken
              </label>
              <div className="flex space-x-4">
                <button
                  onClick={() => setMedicationForm({...medicationForm, taken: true})}
                  className={`flex-1 py-3 px-4 rounded-lg border-2 transition-colors ${
                    medicationForm.taken
                      ? 'border-green-500 bg-green-50 text-green-700'
                      : 'border-gray-300 text-gray-700 hover:border-gray-400'
                  }`}
                >
                  Yes, taken
                </button>
                <button
                  onClick={() => setMedicationForm({...medicationForm, taken: false})}
                  className={`flex-1 py-3 px-4 rounded-lg border-2 transition-colors ${
                    !medicationForm.taken
                      ? 'border-red-500 bg-red-50 text-red-700'
                      : 'border-gray-300 text-gray-700 hover:border-gray-400'
                  }`}
                >
                  Missed dose
                </button>
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Notes (optional)
              </label>
              <textarea
                value={medicationForm.notes}
                onChange={(e) => setMedicationForm({...medicationForm, notes: e.target.value})}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                placeholder="Any side effects or observations..."
                rows={2}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Date
              </label>
              <input
                type="date"
                value={medicationForm.date}
                onChange={(e) => setMedicationForm({...medicationForm, date: e.target.value})}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
              />
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Log Type Selection Tiles */}
      <div className="bg-white border-b border-gray-200 px-4 py-6">
        <div className="max-w-4xl mx-auto">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">What would you like to log?</h2>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            {logTypes.map((logType) => (
              <button
                key={logType.id}
                onClick={() => handleLogTypeChange(logType.id as 'symptom' | 'nutrition' | 'medication')}
                className={`p-6 rounded-xl border-2 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 ${
                  selectedLogType === logType.id
                    ? 'border-indigo-500 bg-indigo-50 shadow-md'
                    : 'border-gray-200 bg-white hover:border-gray-300 hover:shadow-sm'
                }`}
              >
                <div className="text-center">
                  <div className={`inline-flex p-3 rounded-lg ${logType.color} text-white mb-3`}>
                    {logType.icon}
                  </div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-1">{logType.title}</h3>
                  <p className="text-sm text-gray-600">{logType.description}</p>
                </div>
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* AI and Media Capture Tools */}
      <div className="bg-white border-b border-gray-200 px-4 py-4">
        <div className="max-w-4xl mx-auto">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <button
                onClick={() => setShowAIModal(true)}
                className="flex items-center space-x-2 px-4 py-2 bg-indigo-600 text-white rounded-lg font-medium hover:bg-indigo-700 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
              >
                <AIIcon />
                <span>Use AI to fill fields</span>
              </button>
            </div>
            
            <div className="flex items-center space-x-2">
              <button
                className="p-2 text-gray-400 hover:text-gray-600 transition-colors rounded-lg hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 relative group"
                title="Media capture coming soon"
              >
                <CameraIcon />
                <div className="absolute bottom-full right-0 mb-2 px-2 py-1 bg-gray-900 text-white text-xs rounded opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-10">
                  Media capture coming soon
                  <div className="absolute top-full right-2 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-900"></div>
                </div>
              </button>
              <button
                className="p-2 text-gray-400 hover:text-gray-600 transition-colors rounded-lg hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 relative group"
                title="Media capture coming soon"
              >
                <MicrophoneIcon />
                <div className="absolute bottom-full right-0 mb-2 px-2 py-1 bg-gray-900 text-white text-xs rounded opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-10">
                  Media capture coming soon
                  <div className="absolute top-full right-2 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-900"></div>
                </div>
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Form Content */}
      <div className="px-4 py-6">
        <div className="max-w-2xl mx-auto">
          {renderForm()}
        </div>
      </div>

      {/* Footer Actions */}
      <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 px-4 py-4">
        <div className="max-w-2xl mx-auto">
          <div className="flex flex-col sm:flex-row space-y-3 sm:space-y-0 sm:space-x-3">
            <button
              onClick={handleSave}
              className="flex-1 px-6 py-3 bg-indigo-600 text-white rounded-lg font-medium hover:bg-indigo-700 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
            >
              Save
            </button>
            <button
              onClick={handleSaveAndAddAnother}
              className="flex-1 px-6 py-3 border border-gray-300 text-gray-700 rounded-lg font-medium hover:bg-gray-50 transition-colors focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2"
            >
              Save & add another
            </button>
            <button
              onClick={onCancel}
              className="px-6 py-3 text-gray-600 hover:text-gray-800 transition-colors focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2"
            >
              Cancel
            </button>
          </div>
        </div>
      </div>

      {/* AI Modal */}
      {showAIModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-md w-full">
            <div className="p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">AI Assistant</h3>
              <p className="text-sm text-gray-600 mb-6">
                Paste text, upload photo/video, or record voice (coming soon).
              </p>
              
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 mb-6">
                <div className="flex items-start">
                  <div className="flex-shrink-0">
                    <svg className="w-5 h-5 text-yellow-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01M21 12c0 4.418-4.03 8-9 8a9 9 0 110-18c4.97 0 9 3.582 9 8z" />
                    </svg>
                  </div>
                  <div className="ml-3">
                    <p className="text-sm text-yellow-800">
                      <strong>Enable in Settings when available.</strong>
                    </p>
                  </div>
                </div>
              </div>
              
              <div className="flex justify-end space-x-3">
                <button
                  onClick={() => setShowAIModal(false)}
                  className="px-4 py-2 text-gray-600 hover:text-gray-800 transition-colors focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Saved Toast */}
      {showSavedToast && (
        <div className="fixed bottom-20 left-1/2 transform -translate-x-1/2 z-50">
          <div className="bg-green-600 text-white px-6 py-3 rounded-lg shadow-lg flex items-center space-x-2">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <span className="font-medium">Saved successfully!</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default QuickLog;
