import React, { useState } from 'react';

// Icon components
const ChevronDownIcon = () => (
  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
  </svg>
);

const ChevronUpIcon = () => (
  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 15l7-7 7 7" />
  </svg>
);

const MedicationIcon = () => (
  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19.428 15.428a2 2 0 00-1.022-.547l-2.387-.477a6 6 0 00-3.86.517l-.318.158a6 6 0 01-3.86.517L6.05 15.21a2 2 0 00-1.806.547M8 4h8l-1 1v5.172a2 2 0 00.586 1.414l5 5c1.26 1.26.367 3.414-1.415 3.414H4.828c-1.782 0-2.674-2.154-1.414-3.414l5-5A2 2 0 009 10.172V5L8 4z" />
  </svg>
);

const NutritionIcon = () => (
  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4m0 0L7 13m0 0l-2.5 5M7 13l2.5 5m6-5v6a2 2 0 01-2 2H9a2 2 0 01-2-2v-6m8 0V9a2 2 0 00-2-2H9a2 2 0 00-2 2v4.01" />
  </svg>
);

const SymptomIcon = () => (
  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
  </svg>
);

const MoreIcon = () => (
  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z" />
  </svg>
);

const EditIcon = () => (
  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
  </svg>
);

const RefillIcon = () => (
  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
  </svg>
);

const HistoryIcon = () => (
  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
  </svg>
);

const SafetyIcon = () => (
  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
  </svg>
);

const RemoveIcon = () => (
  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
  </svg>
);

const CloseIcon = () => (
  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
  </svg>
);

const DownloadIcon = () => (
  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 10v6m0 0l-3-3m3 3l3-3m2 8H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
  </svg>
);

const Tools: React.FC = () => {
  // State for collapsible sections
  const [expandedSections, setExpandedSections] = useState({
    medication: true, // expanded by default
    nutrition: false,
    symptom: false
  });

  // State for modals
  const [showExportModal, setShowExportModal] = useState(false);
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [showHistoryDrawer, setShowHistoryDrawer] = useState(false);
  const [showEditScheduleDrawer, setShowEditScheduleDrawer] = useState(false);
  const [showAIClarifyModal, setShowAIClarifyModal] = useState(false);
  const [showTimezoneModal, setShowTimezoneModal] = useState(false);
  const [showRefillModal, setShowRefillModal] = useState(false);
  const [showPADrawer, setShowPADrawer] = useState(false);
  const [selectedMedication, setSelectedMedication] = useState<any>(null);
  const [showMoreMenu, setShowMoreMenu] = useState<string | null>(null);
  const [editingQuantity, setEditingQuantity] = useState<string | null>(null);
  
  // Edit Schedule state
  const [editScheduleForm, setEditScheduleForm] = useState({
    directions: '',
    dosingWindows: {
      AM: { enabled: false, time: '08:00', reminderStart: '07:30', reminderEnd: '08:30' },
      Noon: { enabled: false, time: '12:00', reminderStart: '11:30', reminderEnd: '12:30' },
      PM: { enabled: false, time: '18:00', reminderStart: '17:30', reminderEnd: '18:30' },
      HS: { enabled: false, time: '20:00', reminderStart: '19:30', reminderEnd: '20:30' }
    },
    daysOfWeek: {
      Monday: true,
      Tuesday: true,
      Wednesday: true,
      Thursday: true,
      Friday: true,
      Saturday: true,
      Sunday: true
    },
    schoolDosing: false,
    schoolTime: '12:00',
    titrationPlan: []
  });

  // Sample medication data
  const medications = [
    {
      id: '1',
      name: 'Amoxicillin',
      brand: 'Amoxil',
      strength: '125 mg/5 mL',
      form: 'oral suspension',
      route: 'PO',
      schedule: ['AM', 'PM'],
      status: 'Active',
      startDate: '2024-01-15',
      refillDate: '2024-02-15',
      history: [
        { date: '2024-01-15', event: 'Started medication', details: 'Prescribed for ear infection' },
        { date: '2024-01-20', event: 'Dose adjustment', details: 'Increased to 7.5 mL per dose' },
        { date: '2024-02-01', event: 'Refill', details: 'New bottle opened' }
      ]
    },
    {
      id: '2',
      name: 'Albuterol',
      brand: 'Proventil',
      strength: '90 mcg',
      form: 'inhaler',
      route: 'Inhaled',
      schedule: ['As needed'],
      status: 'Active',
      startDate: '2023-12-01',
      refillDate: '2024-03-01',
      history: [
        { date: '2023-12-01', event: 'Started medication', details: 'Prescribed for asthma' },
        { date: '2024-01-15', event: 'Refill', details: 'New inhaler dispensed' }
      ]
    },
    {
      id: '3',
      name: 'Melatonin',
      brand: 'Generic',
      strength: '3 mg',
      form: 'tablet',
      route: 'PO',
      schedule: ['HS'],
      status: 'Hold',
      startDate: '2023-11-01',
      refillDate: '2024-04-01',
      history: [
        { date: '2023-11-01', event: 'Started medication', details: 'Prescribed for sleep' },
        { date: '2024-01-10', event: 'Put on hold', details: 'Sleep improved, temporarily discontinued' }
      ]
    }
  ];

  const toggleSection = (section: keyof typeof expandedSections) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const handleViewHistory = (medication: any) => {
    setSelectedMedication(medication);
    setShowHistoryDrawer(true);
    setShowMoreMenu(null);
  };

  const handleEditSchedule = (medicationId: string) => {
    console.log('Edit schedule for medication:', medicationId);
    setSelectedMedication(medications.find(m => m.id === medicationId));
    setShowEditScheduleDrawer(true);
    setShowMoreMenu(null);
  };

  const handleRefill = (medicationId: string) => {
    console.log('Refill medication:', medicationId);
    setShowMoreMenu(null);
  };

  const handleSafety = (medicationId: string) => {
    console.log('View safety info for medication:', medicationId);
    setShowMoreMenu(null);
  };

  const handleRemove = (medicationId: string) => {
    console.log('Remove medication:', medicationId);
    setShowMoreMenu(null);
  };

  const handleDownloadHistory = (format: 'csv' | 'pdf') => {
    console.log(`Downloading history as ${format} for:`, selectedMedication?.name);
  };

  const handleSaveSchedule = () => {
    console.log('Saving schedule:', editScheduleForm);
    setShowEditScheduleDrawer(false);
    // TODO: Show toast "Schedule updated."
  };

  const handleToggleDosingWindow = (window: string) => {
    setEditScheduleForm(prev => ({
      ...prev,
      dosingWindows: {
        ...prev.dosingWindows,
        [window]: {
          ...prev.dosingWindows[window as keyof typeof prev.dosingWindows],
          enabled: !prev.dosingWindows[window as keyof typeof prev.dosingWindows].enabled
        }
      }
    }));
  };

  const handleUpdateDosingTime = (window: string, field: string, value: string) => {
    setEditScheduleForm(prev => ({
      ...prev,
      dosingWindows: {
        ...prev.dosingWindows,
        [window]: {
          ...prev.dosingWindows[window as keyof typeof prev.dosingWindows],
          [field]: value
        }
      }
    }));
  };

  const handleToggleDayOfWeek = (day: string) => {
    setEditScheduleForm(prev => ({
      ...prev,
      daysOfWeek: {
        ...prev.daysOfWeek,
        [day]: !prev.daysOfWeek[day as keyof typeof prev.daysOfWeek]
      }
    }));
  };

  const handleToggleSchoolDosing = () => {
    setEditScheduleForm(prev => ({
      ...prev,
      schoolDosing: !prev.schoolDosing
    }));
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'Active':
        return 'bg-green-100 text-green-800';
      case 'Hold':
        return 'bg-yellow-100 text-yellow-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getRouteColor = (route: string) => {
    switch (route) {
      case 'PO':
        return 'bg-blue-100 text-blue-800';
      case 'Inhaled':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Tools Content */}
      <div className="px-4 py-6">
        <div className="max-w-4xl mx-auto">
          {/* Single column on mobile, two columns on desktop */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            
            {/* Section 1: Medication Tools */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200">
              <button
                onClick={() => toggleSection('medication')}
                className="w-full px-6 py-4 flex items-center justify-between text-left hover:bg-gray-50 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
              >
                <div className="flex items-center space-x-3">
                  <div className="p-2 bg-indigo-100 rounded-lg">
                    <MedicationIcon />
                  </div>
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900">Medication Tools</h3>
                    <p className="text-sm text-gray-600">Manage medications and dosing</p>
                  </div>
                </div>
                {expandedSections.medication ? <ChevronUpIcon /> : <ChevronDownIcon />}
              </button>
              
              {expandedSections.medication && (
                <div className="px-6 pb-6 border-t border-gray-200">
                  {/* Medication Tools Header */}
                  <div className="pt-4 pb-4 border-b border-gray-200">
                    <div className="flex items-center justify-between">
                      <div>
                        <h4 className="text-lg font-semibold text-gray-900">Medication Tools</h4>
                        <p className="text-sm text-gray-600">Manage schedules, refills, and school/travel readiness.</p>
                      </div>
                      <div className="flex items-center space-x-2">
                        <button
                          onClick={() => setShowExportModal(true)}
                          className="px-3 py-1 text-xs font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                        >
                          Export list
                        </button>
                        <button
                          onClick={() => setShowSettingsModal(true)}
                          className="px-3 py-1 text-xs font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                        >
                          Settings
                        </button>
                      </div>
                    </div>
                  </div>
                  
                  <div className="pt-4 space-y-4">
                    {/* Current Medications */}
                    <div>
                      <h5 className="text-md font-semibold text-gray-900 mb-3">Current Medications</h5>
                      
                      {medications.length > 0 ? (
                        <div className="space-y-3">
                          {medications.map((medication) => (
                            <div key={medication.id} className="bg-gray-50 rounded-lg p-4 border border-gray-200">
                              {/* Header */}
                              <div className="flex items-start justify-between mb-2">
                                <div className="flex-1">
                                  <h6 className="font-medium text-gray-900">
                                    {medication.name} {medication.brand !== 'Generic' && `(${medication.brand})`}
                                  </h6>
                                  <p className="text-sm text-gray-600">
                                    {medication.strength} {medication.form}
                                  </p>
                                </div>
                                <div className="flex items-center space-x-1">
                                  {/* Mini-actions */}
                                  <button
                                    onClick={() => handleEditSchedule(medication.id)}
                                    className="p-1 text-gray-400 hover:text-gray-600 transition-colors"
                                    title="Edit schedule"
                                  >
                                    <EditIcon />
                                  </button>
                                  <button
                                    onClick={() => handleRefill(medication.id)}
                                    className="p-1 text-gray-400 hover:text-gray-600 transition-colors"
                                    title="Refill"
                                  >
                                    <RefillIcon />
                                  </button>
                                  <div className="relative">
                                    <button
                                      onClick={() => setShowMoreMenu(showMoreMenu === medication.id ? null : medication.id)}
                                      className="p-1 text-gray-400 hover:text-gray-600 transition-colors"
                                      title="More options"
                                    >
                                      <MoreIcon />
                                    </button>
                                    
                                    {/* More menu dropdown */}
                                    {showMoreMenu === medication.id && (
                                      <div className="absolute right-0 top-8 w-48 bg-white rounded-lg shadow-lg border border-gray-200 z-10">
                                        <button
                                          onClick={() => handleViewHistory(medication)}
                                          className="w-full px-3 py-2 text-left text-sm text-gray-700 hover:bg-gray-50 flex items-center space-x-2"
                                        >
                                          <HistoryIcon />
                                          <span>View history</span>
                                        </button>
                                        <button
                                          onClick={() => handleSafety(medication.id)}
                                          className="w-full px-3 py-2 text-left text-sm text-gray-700 hover:bg-gray-50 flex items-center space-x-2"
                                        >
                                          <SafetyIcon />
                                          <span>Safety</span>
                                        </button>
                                        <button
                                          onClick={() => handleRemove(medication.id)}
                                          className="w-full px-3 py-2 text-left text-sm text-red-600 hover:bg-red-50 flex items-center space-x-2"
                                        >
                                          <RemoveIcon />
                                          <span>Remove</span>
                                        </button>
                                      </div>
                                    )}
                                  </div>
                                </div>
                              </div>
                              
                              {/* Badges */}
                              <div className="flex flex-wrap gap-2">
                                <span className={`px-2 py-1 text-xs font-medium rounded-full ${getRouteColor(medication.route)}`}>
                                  {medication.route}
                                </span>
                                {medication.schedule.map((time, index) => (
                                  <span key={index} className="px-2 py-1 text-xs font-medium bg-indigo-100 text-indigo-800 rounded-full">
                                    {time}
                                  </span>
                                ))}
                                <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(medication.status)}`}>
                                  {medication.status}
                                </span>
                              </div>
                            </div>
                          ))}
                        </div>
                      ) : (
                        <div className="text-center py-8 text-gray-500">
                          <MedicationIcon />
                          <p className="mt-2 text-sm">No medications yet. Add one from Quick Log &gt; Medication.</p>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              )}
            </div>

            {/* Section 2: Nutrition Tools */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200">
              <button
                onClick={() => toggleSection('nutrition')}
                className="w-full px-6 py-4 flex items-center justify-between text-left hover:bg-gray-50 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
              >
                <div className="flex items-center space-x-3">
                  <div className="p-2 bg-green-100 rounded-lg">
                    <NutritionIcon />
                  </div>
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900">Nutrition Tools</h3>
                    <p className="text-sm text-gray-600">Plan meals and track nutrition</p>
                  </div>
                </div>
                {expandedSections.nutrition ? <ChevronUpIcon /> : <ChevronDownIcon />}
              </button>
              
              {expandedSections.nutrition && (
                <div className="px-6 pb-6 border-t border-gray-200">
                  <div className="pt-4 space-y-4">
                    {/* Nutrition Tools content will be added in Phase 4B */}
                    <div className="text-center py-8 text-gray-500">
                      <NutritionIcon />
                      <p className="mt-2 text-sm">Nutrition tools coming in Phase 4B...</p>
                    </div>
                  </div>
                </div>
              )}
            </div>

            {/* Section 3: Symptom Tools */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 lg:col-span-2">
              <button
                onClick={() => toggleSection('symptom')}
                className="w-full px-6 py-4 flex items-center justify-between text-left hover:bg-gray-50 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
              >
                <div className="flex items-center space-x-3">
                  <div className="p-2 bg-red-100 rounded-lg">
                    <SymptomIcon />
                  </div>
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900">Symptom Tools</h3>
                    <p className="text-sm text-gray-600">Analyze patterns and trends</p>
                  </div>
                </div>
                {expandedSections.symptom ? <ChevronUpIcon /> : <ChevronDownIcon />}
              </button>
              
              {expandedSections.symptom && (
                <div className="px-6 pb-6 border-t border-gray-200">
                  <div className="pt-4 space-y-4">
                    {/* Symptom Tools content will be added in Phase 4C */}
                    <div className="text-center py-8 text-gray-500">
                      <SymptomIcon />
                      <p className="mt-2 text-sm">Symptom tools coming in Phase 4C...</p>
                    </div>
                  </div>
                </div>
              )}
            </div>

          </div>
        </div>
      </div>

      {/* Export Modal */}
      {showExportModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-md w-full">
            <div className="p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Export Medication List</h3>
              <p className="text-sm text-gray-600 mb-6">
                Choose a format to export your medication list.
              </p>
              
              <div className="space-y-3 mb-6">
                <label className="flex items-center">
                  <input type="radio" name="exportFormat" value="pdf" defaultChecked className="mr-3" />
                  <span className="text-sm font-medium text-gray-900">PDF (Summary)</span>
                </label>
                <label className="flex items-center">
                  <input type="radio" name="exportFormat" value="csv" className="mr-3" />
                  <span className="text-sm font-medium text-gray-900">CSV (Raw data)</span>
                </label>
              </div>
              
              <div className="flex justify-end space-x-3">
                <button
                  onClick={() => setShowExportModal(false)}
                  className="px-4 py-2 text-gray-700 bg-white border border-gray-300 rounded-lg font-medium hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  onClick={() => {
                    // TODO: Implement actual export
                    console.log('Exporting medication list...');
                    setShowExportModal(false);
                  }}
                  className="px-4 py-2 bg-indigo-600 text-white rounded-lg font-medium hover:bg-indigo-700"
                >
                  Export
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Settings Modal */}
      {showSettingsModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-md w-full">
            <div className="p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Reminder Settings</h3>
              <p className="text-sm text-gray-600 mb-6">
                Configure medication reminder preferences.
              </p>
              
              <div className="space-y-4 mb-6">
                <label className="flex items-center">
                  <input type="checkbox" defaultChecked className="mr-3" />
                  <span className="text-sm font-medium text-gray-900">Enable medication reminders</span>
                </label>
                <label className="flex items-center">
                  <input type="checkbox" defaultChecked className="mr-3" />
                  <span className="text-sm font-medium text-gray-900">Notify before refills</span>
                </label>
                <label className="flex items-center">
                  <input type="checkbox" className="mr-3" />
                  <span className="text-sm font-medium text-gray-900">School/travel notifications</span>
                </label>
              </div>
              
              <div className="flex justify-end space-x-3">
                <button
                  onClick={() => setShowSettingsModal(false)}
                  className="px-4 py-2 text-gray-700 bg-white border border-gray-300 rounded-lg font-medium hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  onClick={() => {
                    // TODO: Implement actual settings save
                    console.log('Saving reminder settings...');
                    setShowSettingsModal(false);
                  }}
                  className="px-4 py-2 bg-indigo-600 text-white rounded-lg font-medium hover:bg-indigo-700"
                >
                  Save
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* History Drawer */}
      {showHistoryDrawer && selectedMedication && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-end lg:items-center justify-end z-50">
          <div className="bg-white w-full lg:w-96 h-full lg:h-auto lg:max-h-[80vh] lg:rounded-l-xl shadow-xl flex flex-col">
            {/* Header */}
            <div className="flex items-center justify-between p-6 border-b border-gray-200">
              <div>
                <h3 className="text-lg font-semibold text-gray-900">Medication History</h3>
                <p className="text-sm text-gray-600">{selectedMedication.name}</p>
              </div>
              <button
                onClick={() => setShowHistoryDrawer(false)}
                className="p-1 text-gray-400 hover:text-gray-600 transition-colors"
              >
                <CloseIcon />
              </button>
            </div>
            
            {/* Content */}
            <div className="flex-1 overflow-y-auto p-6">
              <div className="space-y-4">
                <div>
                  <h4 className="font-medium text-gray-900 mb-2">Start Date</h4>
                  <p className="text-sm text-gray-600">{selectedMedication.startDate}</p>
                </div>
                
                <div>
                  <h4 className="font-medium text-gray-900 mb-2">History</h4>
                  <div className="space-y-3">
                    {selectedMedication.history.map((event: any, index: number) => (
                      <div key={index} className="bg-gray-50 rounded-lg p-3">
                        <div className="flex items-start justify-between">
                          <div className="flex-1">
                            <p className="text-sm font-medium text-gray-900">{event.event}</p>
                            <p className="text-xs text-gray-600">{event.details}</p>
                          </div>
                          <span className="text-xs text-gray-500">{event.date}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
            
            {/* Footer */}
            <div className="p-6 border-t border-gray-200">
              <button
                onClick={() => handleDownloadHistory('csv')}
                className="w-full px-4 py-2 bg-indigo-600 text-white rounded-lg font-medium hover:bg-indigo-700 flex items-center justify-center space-x-2"
              >
                <DownloadIcon />
                <span>Download History (CSV/PDF)</span>
              </button>
            </div>
          </div>
                 </div>
       )}

       {/* Edit Schedule Drawer */}
       {showEditScheduleDrawer && selectedMedication && (
         <div className="fixed inset-0 bg-black bg-opacity-50 flex items-end lg:items-center justify-end z-50">
           <div className="bg-white w-full lg:w-[600px] h-full lg:h-auto lg:max-h-[90vh] lg:rounded-l-xl shadow-xl flex flex-col">
             {/* Header */}
             <div className="flex items-center justify-between p-6 border-b border-gray-200">
               <div>
                 <h3 className="text-lg font-semibold text-gray-900">Edit Schedule</h3>
                 <p className="text-sm text-gray-600">{selectedMedication.name}</p>
               </div>
               <button
                 onClick={() => setShowEditScheduleDrawer(false)}
                 className="p-1 text-gray-400 hover:text-gray-600 transition-colors"
               >
                 <CloseIcon />
               </button>
             </div>
             
             {/* Content */}
             <div className="flex-1 overflow-y-auto p-6">
               <div className="space-y-6">
                 {/* Directions */}
                 <div>
                   <label className="block text-sm font-medium text-gray-900 mb-2">
                     Directions (SIG)
                   </label>
                   <textarea
                     value={editScheduleForm.directions}
                     onChange={(e) => setEditScheduleForm(prev => ({ ...prev, directions: e.target.value }))}
                     placeholder="e.g., 5 mL twice daily with meals"
                     className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                     rows={3}
                   />
                   <button
                     onClick={() => setShowAIClarifyModal(true)}
                     className="mt-2 text-sm text-indigo-600 hover:text-indigo-700 font-medium"
                   >
                     Clarify SIG with AI
                   </button>
                 </div>

                 {/* Dosing Windows */}
                 <div>
                   <h4 className="text-md font-medium text-gray-900 mb-3">Dosing Windows</h4>
                   <div className="space-y-3">
                     {Object.entries(editScheduleForm.dosingWindows).map(([window, config]) => (
                       <div key={window} className="border border-gray-200 rounded-lg p-4">
                         <div className="flex items-center justify-between mb-3">
                           <label className="flex items-center">
                             <input
                               type="checkbox"
                               checked={config.enabled}
                               onChange={() => handleToggleDosingWindow(window)}
                               className="mr-3 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
                             />
                             <span className="text-sm font-medium text-gray-900">{window}</span>
                           </label>
                         </div>
                         
                         {config.enabled && (
                           <div className="space-y-3 ml-6">
                             <div>
                               <label className="block text-xs font-medium text-gray-700 mb-1">Exact time</label>
                               <input
                                 type="time"
                                 value={config.time}
                                 onChange={(e) => handleUpdateDosingTime(window, 'time', e.target.value)}
                                 className="w-full px-2 py-1 text-sm border border-gray-300 rounded focus:outline-none focus:ring-1 focus:ring-indigo-500"
                               />
                             </div>
                             <div className="grid grid-cols-2 gap-3">
                               <div>
                                 <label className="block text-xs font-medium text-gray-700 mb-1">Reminder start</label>
                                 <input
                                   type="time"
                                   value={config.reminderStart}
                                   onChange={(e) => handleUpdateDosingTime(window, 'reminderStart', e.target.value)}
                                   className="w-full px-2 py-1 text-sm border border-gray-300 rounded focus:outline-none focus:ring-1 focus:ring-indigo-500"
                                 />
                               </div>
                               <div>
                                 <label className="block text-xs font-medium text-gray-700 mb-1">Reminder end</label>
                                 <input
                                   type="time"
                                   value={config.reminderEnd}
                                   onChange={(e) => handleUpdateDosingTime(window, 'reminderEnd', e.target.value)}
                                   className="w-full px-2 py-1 text-sm border border-gray-300 rounded focus:outline-none focus:ring-1 focus:ring-indigo-500"
                                 />
                               </div>
                             </div>
                           </div>
                         )}
                       </div>
                     ))}
                   </div>
                 </div>

                 {/* Days of Week */}
                 <div>
                   <h4 className="text-md font-medium text-gray-900 mb-3">Days of Week</h4>
                   <div className="flex flex-wrap gap-2">
                     {Object.entries(editScheduleForm.daysOfWeek).map(([day, enabled]) => (
                       <button
                         key={day}
                         onClick={() => handleToggleDayOfWeek(day)}
                         className={`px-3 py-1 text-sm font-medium rounded-full transition-colors ${
                           enabled
                             ? 'bg-indigo-100 text-indigo-800 border border-indigo-200'
                             : 'bg-gray-100 text-gray-600 border border-gray-200 hover:bg-gray-200'
                         }`}
                       >
                         {day.slice(0, 2)}
                       </button>
                     ))}
                   </div>
                 </div>

                 {/* School Dosing */}
                 <div>
                   <div className="flex items-center justify-between">
                     <label className="flex items-center">
                       <input
                         type="checkbox"
                         checked={editScheduleForm.schoolDosing}
                         onChange={handleToggleSchoolDosing}
                         className="mr-3 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
                       />
                       <span className="text-sm font-medium text-gray-900">School dosing?</span>
                     </label>
                   </div>
                   
                   {editScheduleForm.schoolDosing && (
                     <div className="mt-3 ml-6 space-y-3">
                       <div>
                         <label className="block text-xs font-medium text-gray-700 mb-1">School time</label>
                         <input
                           type="time"
                           value={editScheduleForm.schoolTime}
                           onChange={(e) => setEditScheduleForm(prev => ({ ...prev, schoolTime: e.target.value }))}
                           className="w-full px-2 py-1 text-sm border border-gray-300 rounded focus:outline-none focus:ring-1 focus:ring-indigo-500"
                         />
                       </div>
                       <button className="px-3 py-1 text-sm font-medium text-indigo-600 bg-indigo-50 border border-indigo-200 rounded-lg hover:bg-indigo-100">
                         Generate nurse form
                       </button>
                     </div>
                   )}
                 </div>

                 {/* Taper/Titration Plan */}
                 <div>
                   <div className="flex items-center justify-between mb-3">
                     <h4 className="text-md font-medium text-gray-900">Taper/Titration Plan</h4>
                     <button className="px-3 py-1 text-sm font-medium text-indigo-600 bg-indigo-50 border border-indigo-200 rounded-lg hover:bg-indigo-100">
                       Add titration
                     </button>
                   </div>
                   <div className="text-sm text-gray-600">
                     No titration plan configured
                   </div>
                 </div>

                 {/* Travel Time Zone Helper */}
                 <div>
                   <button
                     onClick={() => setShowTimezoneModal(true)}
                     className="text-sm text-indigo-600 hover:text-indigo-700 font-medium"
                   >
                     Travel time-zone shift helper
                   </button>
                 </div>
               </div>
             </div>
             
             {/* Footer */}
             <div className="p-6 border-t border-gray-200">
               <div className="flex justify-end space-x-3">
                 <button
                   onClick={() => setShowEditScheduleDrawer(false)}
                   className="px-4 py-2 text-gray-700 bg-white border border-gray-300 rounded-lg font-medium hover:bg-gray-50"
                 >
                   Cancel
                 </button>
                 <button
                   onClick={handleSaveSchedule}
                   className="px-4 py-2 bg-indigo-600 text-white rounded-lg font-medium hover:bg-indigo-700"
                 >
                   Save
                 </button>
               </div>
             </div>
           </div>
         </div>
       )}

       {/* AI Clarify Modal */}
       {showAIClarifyModal && (
         <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
           <div className="bg-white rounded-xl shadow-xl max-w-md w-full">
             <div className="p-6">
               <h3 className="text-lg font-semibold text-gray-900 mb-4">Clarify SIG with AI</h3>
               <p className="text-sm text-gray-600 mb-6">
                 Coming soonWe'll parse the label (SPL/DailyMed) to help structure schedules.
               </p>
               <div className="flex justify-end">
                 <button
                   onClick={() => setShowAIClarifyModal(false)}
                   className="px-4 py-2 bg-indigo-600 text-white rounded-lg font-medium hover:bg-indigo-700"
                 >
                   Close
                 </button>
               </div>
             </div>
           </div>
         </div>
       )}

       {/* Timezone Modal */}
       {showTimezoneModal && (
         <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
           <div className="bg-white rounded-xl shadow-xl max-w-md w-full">
             <div className="p-6">
               <h3 className="text-lg font-semibold text-gray-900 mb-4">Travel Time Zone Helper</h3>
               <p className="text-sm text-gray-600 mb-6">
                 Choose destination time zone and travel date to get recommended shift plan.
               </p>
               <div className="space-y-4 mb-6">
                 <div>
                   <label className="block text-sm font-medium text-gray-700 mb-2">Destination Time Zone</label>
                   <select className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500">
                     <option>Select time zone...</option>
                     <option>Eastern Time (ET)</option>
                     <option>Central Time (CT)</option>
                     <option>Mountain Time (MT)</option>
                     <option>Pacific Time (PT)</option>
                   </select>
                 </div>
                 <div>
                   <label className="block text-sm font-medium text-gray-700 mb-2">Travel Date</label>
                   <input
                     type="date"
                     className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                   />
                 </div>
               </div>
               <div className="bg-gray-50 rounded-lg p-4 mb-6">
                 <p className="text-sm text-gray-600">
                   Recommended shift plan will appear here based on your selection.
                 </p>
               </div>
               <div className="flex justify-end space-x-3">
                 <button
                   onClick={() => setShowTimezoneModal(false)}
                   className="px-4 py-2 text-gray-700 bg-white border border-gray-300 rounded-lg font-medium hover:bg-gray-50"
                 >
                   Cancel
                 </button>
                 <button
                   onClick={() => setShowTimezoneModal(false)}
                   className="px-4 py-2 bg-indigo-600 text-white rounded-lg font-medium hover:bg-indigo-700"
                 >
                   Apply
                 </button>
               </div>
             </div>
           </div>
         </div>
       )}
     </div>
   );
 };

export default Tools;
