import React, { useState } from 'react';

const GrowthDevelopmentScreen: React.FC = () => {
  const [showReportModal, setShowReportModal] = useState(false);
  const [useDevelopmentalLevel, setUseDevelopmentalLevel] = useState(false);
  const [chronologicalAge, setChronologicalAge] = useState('6 months');
  const [developmentalLevel, setDevelopmentalLevel] = useState('Early');
  const [timeScope, setTimeScope] = useState('30 days');

  // Add Skill Quick Sheet states
  const [showAddSkillSheet, setShowAddSkillSheet] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);
  const [selectedDomain, setSelectedDomain] = useState('');
  const [selectedSkill, setSelectedSkill] = useState('');
  const [skillStatus, setSkillStatus] = useState('Observed');
  const [dateObserved, setDateObserved] = useState(new Date().toISOString().split('T')[0]);
  const [supportsUsed, setSupportsUsed] = useState<string[]>([]);
  const [context, setContext] = useState<string[]>([]);
  const [notes, setNotes] = useState('');
  const [showAllSkills, setShowAllSkills] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  // Sample domain data
  const domains = [
    {
      id: 'motor',
      name: 'Motor',
      subtitle: 'Gross & Fine',
      icon: 'üèÉ‚Äç‚ôÄÔ∏è',
      observed: 8,
      suggested: 12,
      lastSkill: 'Rolls both ways',
      lastDate: '2024-03-15'
    },
    {
      id: 'communication',
      name: 'Communication',
      subtitle: 'Expressive & Receptive',
      icon: 'üí¨',
      observed: 5,
      suggested: 10,
      lastSkill: 'Uses 3 words',
      lastDate: '2024-03-12'
    },
    {
      id: 'social-emotional',
      name: 'Social-Emotional',
      subtitle: '',
      icon: 'üòä',
      observed: 6,
      suggested: 8,
      lastSkill: 'Social smile',
      lastDate: '2024-03-10'
    },
    {
      id: 'cognitive-play',
      name: 'Cognitive/Play',
      subtitle: '',
      icon: 'üß©',
      observed: 4,
      suggested: 9,
      lastSkill: 'Bangs objects together',
      lastDate: '2024-03-08'
    },
    {
      id: 'adaptive-self-help',
      name: 'Adaptive/Self-Help',
      subtitle: 'feeding, dressing, toileting',
      icon: 'üëï',
      observed: 3,
      suggested: 7,
      lastSkill: 'Finger feeds',
      lastDate: '2024-03-05'
    }
  ];

  // Sample skills data with proper age band mapping
  const allSkills = [
    // Motor skills
    { id: 'm1', domain: 'motor', name: 'Rolls both ways', ageBand: 'Early', description: 'Rolls from back to stomach and stomach to back' },
    { id: 'm2', domain: 'motor', name: 'Sits briefly without support', ageBand: 'Early', description: 'Maintains sitting position for a few seconds' },
    { id: 'm3', domain: 'motor', name: 'Pulls to stand', ageBand: 'Toddler', description: 'Pulls self up to standing position using furniture' },
    { id: 'm4', domain: 'motor', name: 'Cruises along furniture', ageBand: 'Toddler', description: 'Walks sideways while holding onto furniture' },
    { id: 'm5', domain: 'motor', name: 'Walks with support', ageBand: 'Toddler', description: 'Takes steps while holding hands or walker' },
    { id: 'm6', domain: 'motor', name: 'Pincer grasp', ageBand: 'Early', description: 'Picks up small objects using thumb and index finger' },
    { id: 'm7', domain: 'motor', name: 'Stacks 2 blocks', ageBand: 'Toddler', description: 'Places one block on top of another' },
    { id: 'm8', domain: 'motor', name: 'Throws ball forward', ageBand: 'Toddler', description: 'Throws ball in forward direction' },
    { id: 'm9', domain: 'motor', name: 'Runs with coordination', ageBand: 'Preschool', description: 'Runs with improved balance and coordination' },
    { id: 'm10', domain: 'motor', name: 'Hops on one foot', ageBand: 'Preschool', description: 'Hops briefly on one foot' },
    { id: 'm11', domain: 'motor', name: 'Catches large ball', ageBand: 'Preschool', description: 'Catches ball with arms extended' },
    { id: 'm12', domain: 'motor', name: 'Uses scissors', ageBand: 'Preschool', description: 'Cuts paper with child-safe scissors' },

    // Communication skills
    { id: 'c1', domain: 'communication', name: 'Babbles with consonants', ageBand: 'Early', description: 'Makes consonant sounds like ba, da, ma' },
    { id: 'c2', domain: 'communication', name: 'Points to request', ageBand: 'Toddler', description: 'Points to objects to indicate wants' },
    { id: 'c3', domain: 'communication', name: 'Uses 5-10 words', ageBand: 'Toddler', description: 'Says 5-10 recognizable words' },
    { id: 'c4', domain: 'communication', name: 'Follows 1-step directions', ageBand: 'Toddler', description: 'Understands and follows simple commands' },
    { id: 'c5', domain: 'communication', name: 'Names common objects', ageBand: 'Preschool', description: 'Identifies familiar objects by name' },
    { id: 'c6', domain: 'communication', name: 'Combines 2 words', ageBand: 'Preschool', description: 'Uses two-word phrases' },
    { id: 'c7', domain: 'communication', name: 'Asks questions', ageBand: 'Preschool', description: 'Asks what, where, why questions' },
    { id: 'c8', domain: 'communication', name: 'Tells simple stories', ageBand: 'School-age', description: 'Recounts events in sequence' },
    { id: 'c9', domain: 'communication', name: 'Uses past tense', ageBand: 'School-age', description: 'Correctly uses past tense verbs' },
    { id: 'c10', domain: 'communication', name: 'Follows 3-step directions', ageBand: 'School-age', description: 'Understands and follows multi-step commands' },

    // Social-Emotional skills
    { id: 's1', domain: 'social-emotional', name: 'Social smile', ageBand: 'Early', description: 'Smiles in response to social interaction' },
    { id: 's2', domain: 'social-emotional', name: 'Joint attention', ageBand: 'Early', description: 'Looks where you point' },
    { id: 's3', domain: 'social-emotional', name: 'Waves bye', ageBand: 'Toddler', description: 'Waves goodbye' },
    { id: 's4', domain: 'social-emotional', name: 'Brings item to show', ageBand: 'Toddler', description: 'Shows objects to others' },
    { id: 's5', domain: 'social-emotional', name: 'Parallel play', ageBand: 'Preschool', description: 'Plays alongside other children' },
    { id: 's6', domain: 'social-emotional', name: 'Takes turns briefly', ageBand: 'Preschool', description: 'Participates in simple turn-taking' },
    { id: 's7', domain: 'social-emotional', name: 'Shows empathy', ageBand: 'Preschool', description: 'Comforts others when upset' },
    { id: 's8', domain: 'social-emotional', name: 'Shares toys', ageBand: 'Preschool', description: 'Willingly shares toys with peers' },
    { id: 's9', domain: 'social-emotional', name: 'Manages emotions', ageBand: 'School-age', description: 'Uses strategies to calm down' },
    { id: 's10', domain: 'social-emotional', name: 'Makes friends', ageBand: 'School-age', description: 'Initiates and maintains friendships' },

    // Cognitive/Play skills
    { id: 'cp1', domain: 'cognitive-play', name: 'Bangs two objects', ageBand: 'Early', description: 'Hits two objects together' },
    { id: 'cp2', domain: 'cognitive-play', name: 'Puts objects in/out', ageBand: 'Toddler', description: 'Places objects in containers and removes them' },
    { id: 'cp3', domain: 'cognitive-play', name: 'Cause-and-effect toy play', ageBand: 'Toddler', description: 'Understands simple cause and effect' },
    { id: 'cp4', domain: 'cognitive-play', name: 'Matches shapes', ageBand: 'Preschool', description: 'Identifies and matches similar shapes' },
    { id: 'cp5', domain: 'cognitive-play', name: 'Completes 3-piece puzzle', ageBand: 'Preschool', description: 'Assembles simple puzzles' },
    { id: 'cp6', domain: 'cognitive-play', name: 'Pretend feeds doll', ageBand: 'Preschool', description: 'Engages in pretend play' },
    { id: 'cp7', domain: 'cognitive-play', name: 'Counts to 10', ageBand: 'Preschool', description: 'Recites numbers 1-10' },
    { id: 'cp8', domain: 'cognitive-play', name: 'Recognizes colors', ageBand: 'Preschool', description: 'Names basic colors' },
    { id: 'cp9', domain: 'cognitive-play', name: 'Understands time concepts', ageBand: 'School-age', description: 'Grasps yesterday, today, tomorrow' },
    { id: 'cp10', domain: 'cognitive-play', name: 'Solves simple problems', ageBand: 'School-age', description: 'Finds solutions to everyday challenges' },

    // Adaptive/Self-Help skills
    { id: 'a1', domain: 'adaptive-self-help', name: 'Finger feeds', ageBand: 'Early', description: 'Picks up food with fingers and eats' },
    { id: 'a2', domain: 'adaptive-self-help', name: 'Drinks from open cup with help', ageBand: 'Toddler', description: 'Uses open cup with assistance' },
    { id: 'a3', domain: 'adaptive-self-help', name: 'Uses spoon with some spills', ageBand: 'Toddler', description: 'Attempts to use spoon independently' },
    { id: 'a4', domain: 'adaptive-self-help', name: 'Removes socks', ageBand: 'Toddler', description: 'Takes off socks without help' },
    { id: 'a5', domain: 'adaptive-self-help', name: 'Helps with dressing', ageBand: 'Preschool', description: 'Participates in getting dressed' },
    { id: 'a6', domain: 'adaptive-self-help', name: 'Indicates wet/soiled diaper', ageBand: 'Toddler', description: 'Shows awareness of diaper state' },
    { id: 'a7', domain: 'adaptive-self-help', name: 'Sits on toilet with support', ageBand: 'Preschool', description: 'Sits on toilet with assistance' },
    { id: 'a8', domain: 'adaptive-self-help', name: 'Washes hands', ageBand: 'Preschool', description: 'Washes hands with minimal help' },
    { id: 'a9', domain: 'adaptive-self-help', name: 'Brushes teeth with help', ageBand: 'Preschool', description: 'Participates in tooth brushing' },
    { id: 'a10', domain: 'adaptive-self-help', name: 'Packs backpack', ageBand: 'School-age', description: 'Prepares school materials independently' }
  ];

  const supportsOptions = ['hand-over-hand', 'adaptive utensil', 'prompts', 'AAC', 'brace/orthotics'];
  const contextOptions = ['home', 'school', 'therapy', 'community'];

  // Helper function to get age band from chronological age
  const getAgeBandFromChronological = (age: string): string => {
    if (age.includes('months')) {
      const months = parseInt(age.split(' ')[0]);
      if (months <= 12) return 'Early';
      if (months <= 36) return 'Toddler';
      if (months <= 60) return 'Preschool';
      return 'School-age';
    } else if (age.includes('years')) {
      const years = parseInt(age.split(' ')[0]);
      if (years <= 5) return 'Preschool';
      return 'School-age';
    }
    return 'Early'; // default
  };

  // Helper function to check if skill is within ¬±1 age band
  const isSkillAgeAppropriate = (skillAgeBand: string, selectedAgeBand: string): boolean => {
    const ageBands = ['Early', 'Toddler', 'Preschool', 'School-age'];
    const skillIndex = ageBands.indexOf(skillAgeBand);
    const selectedIndex = ageBands.indexOf(selectedAgeBand);

    if (skillIndex === -1 || selectedIndex === -1) return true; // fallback

    return Math.abs(skillIndex - selectedIndex) <= 1;
  };

  const handleOpenDomain = (domainId: string) => {
    // TODO: Route to domain detail (will be implemented in 5B)
    console.log(`Opening domain: ${domainId}`);
  };

  const handleAddSkill = () => {
    setShowAddSkillSheet(true);
    setCurrentStep(1);
    setSelectedDomain('');
    setSelectedSkill('');
    setSkillStatus('Observed');
    setDateObserved(new Date().toISOString().split('T')[0]);
    setSupportsUsed([]);
    setContext([]);
    setNotes('');
    setShowAllSkills(false);
    setSearchQuery('');
  };

  const handleNextStep = () => {
    if (currentStep < 6) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handlePrevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSave = () => {
    // TODO: Save skill data
    console.log('Saving skill:', { selectedDomain, selectedSkill, skillStatus, dateObserved, supportsUsed, context, notes });
    setShowAddSkillSheet(false);
    // TODO: Show toast: "Skill saved. View it in Journal or the domain page."
  };

  const handleSaveAndAddAnother = () => {
    // TODO: Save skill data
    console.log('Saving skill and adding another:', { selectedDomain, selectedSkill, skillStatus, dateObserved, supportsUsed, context, notes });
    // Reset form for next skill
    setSelectedSkill('');
    setSkillStatus('Observed');
    setDateObserved(new Date().toISOString().split('T')[0]);
    setSupportsUsed([]);
    setContext([]);
    setNotes('');
    setCurrentStep(2); // Go back to skill selection
  };

  // Get current age band for filtering
  const currentAgeBand = useDevelopmentalLevel ? developmentalLevel : getAgeBandFromChronological(chronologicalAge);

  const filteredSkills = allSkills.filter(skill => {
    if (selectedDomain && skill.domain !== selectedDomain) return false;
    if (searchQuery && !skill.name.toLowerCase().includes(searchQuery.toLowerCase())) return false;
    return true;
  });

  // Filter by age appropriateness (default: ¬±1 band)
  const ageAppropriateSkills = showAllSkills ? filteredSkills : filteredSkills.filter(skill => 
    isSkillAgeAppropriate(skill.ageBand, currentAgeBand)
  );

  const suggestedSkills = ageAppropriateSkills.slice(0, 10);
  const displayedSkills = showAllSkills ? ageAppropriateSkills : suggestedSkills;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-20 py-4">
            <div>
              <h1 className="text-2xl font-semibold text-gray-900">
                Growth & Milestones
              </h1>
              <p className="text-sm text-gray-500 mt-1">
                Track observed skills across domains at your child's pace.
              </p>
            </div>

            <div className="flex items-center space-x-3">
              {/* Info Icon */}
              <div className="relative group">
                <button className="p-2 text-gray-400 hover:text-gray-600 transition-colors">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                </button>
                {/* Tooltip */}
                <div className="absolute bottom-full right-0 mb-2 px-3 py-2 bg-gray-900 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none whitespace-nowrap z-10">
                  Not a diagnostic tool
                  <div className="absolute top-full right-4 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-900"></div>
                </div>
              </div>

              {/* Secondary Action */}
              <button
                onClick={() => setShowReportModal(true)}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                Generate Milestone Report
              </button>

              {/* Primary Action */}
              <button 
                onClick={handleAddSkill}
                className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                Add Skill
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Filter Bar */}
      <div className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-3 sm:space-y-0 sm:space-x-6">
            {/* Age Selector */}
            <div className="flex items-center space-x-3">
              <label className="text-sm font-medium text-gray-700">Age:</label>
              {useDevelopmentalLevel ? (
                <select
                  value={developmentalLevel}
                  onChange={(e) => setDevelopmentalLevel(e.target.value)}
                  className="text-sm border border-gray-300 rounded-md px-3 py-1.5 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                >
                  <option value="Early">Early (0-12m)</option>
                  <option value="Toddler">Toddler (12-36m)</option>
                  <option value="Preschool">Preschool (3-5y)</option>
                  <option value="School-age">School-age (5+)</option>
                </select>
              ) : (
                <select
                  value={chronologicalAge}
                  onChange={(e) => setChronologicalAge(e.target.value)}
                  className="text-sm border border-gray-300 rounded-md px-3 py-1.5 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                >
                  <option value="6 months">6 months</option>
                  <option value="12 months">12 months</option>
                  <option value="18 months">18 months</option>
                  <option value="24 months">24 months</option>
                  <option value="3 years">3 years</option>
                  <option value="4 years">4 years</option>
                  <option value="5 years">5 years</option>
                  <option value="6 years">6 years</option>
                </select>
              )}
            </div>

            {/* Developmental Level Toggle */}
            <div className="flex items-center space-x-3">
              <label className="text-sm font-medium text-gray-700">Use developmental level instead:</label>
              <button
                onClick={() => setUseDevelopmentalLevel(!useDevelopmentalLevel)}
                className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 ${
                  useDevelopmentalLevel ? 'bg-blue-600' : 'bg-gray-200'
                }`}
              >
                <span
                  className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                    useDevelopmentalLevel ? 'translate-x-6' : 'translate-x-1'
                  }`}
                />
              </button>
            </div>

            {/* Time Scope */}
            <div className="flex items-center space-x-3">
              <label className="text-sm font-medium text-gray-700">Time scope:</label>
              <select
                value={timeScope}
                onChange={(e) => setTimeScope(e.target.value)}
                className="text-sm border border-gray-300 rounded-md px-3 py-1.5 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="This week">This week</option>
                <option value="30 days">30 days</option>
                <option value="90 days">90 days</option>
                <option value="Custom">Custom</option>
              </select>
            </div>
          </div>
        </div>
      </div>

      {/* Domain Tiles */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-6">
          {domains.map((domain) => {
            const progressPercentage = (domain.observed / domain.suggested) * 100;
            const circumference = 2 * Math.PI * 18; // radius = 18
            const strokeDasharray = circumference;
            const strokeDashoffset = circumference - (progressPercentage / 100) * circumference;

            return (
              <div key={domain.id} className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
                {/* Domain Header */}
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-center space-x-3">
                    <div className="text-3xl">{domain.icon}</div>
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">{domain.name}</h3>
                      {domain.subtitle && (
                        <p className="text-sm text-gray-500">{domain.subtitle}</p>
                      )}
                    </div>
                  </div>
                </div>

                {/* Progress Ring */}
                <div className="flex items-center justify-center mb-4">
                  <div className="relative">
                    <svg className="w-16 h-16 transform -rotate-90" viewBox="0 0 36 36">
                      {/* Background circle */}
                      <circle
                        cx="18"
                        cy="18"
                        r="18"
                        fill="none"
                        stroke="#e5e7eb"
                        strokeWidth="3"
                      />
                      {/* Progress circle */}
                      <circle
                        cx="18"
                        cy="18"
                        r="18"
                        fill="none"
                        stroke="#3b82f6"
                        strokeWidth="3"
                        strokeDasharray={strokeDasharray}
                        strokeDashoffset={strokeDashoffset}
                        strokeLinecap="round"
                        className="transition-all duration-300"
                      />
                    </svg>
                    <div className="absolute inset-0 flex items-center justify-center">
                      <span className="text-sm font-medium text-gray-900">
                        {domain.observed}/{domain.suggested}
                      </span>
                    </div>
                  </div>
                </div>

                {/* Progress Text */}
                <p className="text-sm text-gray-600 text-center mb-4">
                  Observed {domain.observed} of {domain.suggested} suggested skills
                </p>

                {/* Last Skill Info */}
                <div className="text-center mb-4">
                  {domain.lastSkill ? (
                    <p className="text-sm text-gray-500">
                      Last new skill: <span className="font-medium text-gray-700">{domain.lastSkill}</span> on {domain.lastDate}
                    </p>
                  ) : (
                    <p className="text-sm text-gray-500">No skills yet</p>
                  )}
                </div>

                {/* CTA Button */}
                <button
                  onClick={() => handleOpenDomain(domain.id)}
                  className="w-full px-4 py-2 text-sm font-medium text-blue-600 bg-blue-50 border border-blue-200 rounded-md hover:bg-blue-100 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors"
                >
                  Open
                </button>
              </div>
            );
          })}
        </div>
      </div>

      {/* Add Skill Quick Sheet Modal */}
      {showAddSkillSheet && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-10 mx-auto p-6 border w-full max-w-2xl shadow-lg rounded-md bg-white">
            {/* Header */}
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-semibold text-gray-900">Add Skill</h2>
              <button
                onClick={() => setShowAddSkillSheet(false)}
                className="text-gray-400 hover:text-gray-600"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            {/* Progress Steps */}
            <div className="flex items-center justify-between mb-6">
              {[1, 2, 3, 4, 5, 6].map((step) => (
                <div key={step} className="flex items-center">
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                    step === currentStep 
                      ? 'bg-blue-600 text-white' 
                      : step < currentStep 
                        ? 'bg-green-500 text-white' 
                        : 'bg-gray-200 text-gray-600'
                  }`}>
                    {step < currentStep ? '‚úì' : step}
                  </div>
                  {step < 6 && (
                    <div className={`w-16 h-1 mx-2 ${
                      step < currentStep ? 'bg-green-500' : 'bg-gray-200'
                    }`} />
                  )}
                </div>
              ))}
            </div>

            {/* Step Content */}
            <div className="min-h-64">
              {/* Step 1: Choose Domain */}
              {currentStep === 1 && (
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Choose Domain</h3>
                  <div className="grid grid-cols-2 gap-3">
                    {domains.map((domain) => (
                      <button
                        key={domain.id}
                        onClick={() => {
                          setSelectedDomain(domain.id);
                          handleNextStep();
                        }}
                        className={`p-4 border rounded-lg text-left hover:border-blue-300 transition-colors ${
                          selectedDomain === domain.id ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                        }`}
                      >
                        <div className="text-2xl mb-2">{domain.icon}</div>
                        <div className="font-medium text-gray-900">{domain.name}</div>
                        {domain.subtitle && (
                          <div className="text-sm text-gray-500">{domain.subtitle}</div>
                        )}
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {/* Step 2: Select Skill */}
              {currentStep === 2 && (
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Select Skill</h3>
                  <div className="mb-4">
                    <input
                      type="text"
                      placeholder="Search skills..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>
                  <div className="space-y-2 max-h-48 overflow-y-auto">
                    {displayedSkills.map((skill) => (
                      <button
                        key={skill.id}
                        onClick={() => {
                          setSelectedSkill(skill.id);
                          handleNextStep();
                        }}
                        className={`w-full p-3 text-left border rounded-lg hover:border-blue-300 transition-colors ${
                          selectedSkill === skill.id ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                        }`}
                      >
                        <div className="font-medium text-gray-900">{skill.name}</div>
                        <div className="text-sm text-gray-500">{skill.description}</div>
                        <div className="text-xs text-gray-400 mt-1">{skill.ageBand}</div>
                      </button>
                    ))}
                  </div>
                  {filteredSkills.length > 10 && (
                    <button
                      onClick={() => setShowAllSkills(!showAllSkills)}
                      className="mt-3 text-blue-600 hover:text-blue-700 text-sm"
                    >
                      {showAllSkills ? 'Show top 10' : `Show all ${filteredSkills.length} skills in domain`}
                    </button>
                  )}
                </div>
              )}

              {/* Step 3: Mark Status */}
              {currentStep === 3 && (
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Mark Status</h3>
                  <div className="space-y-3">
                    {['Observed', 'Emerging', 'Not yet', 'Not applicable'].map((status) => (
                      <label key={status} className="flex items-center">
                        <input
                          type="radio"
                          name="status"
                          value={status}
                          checked={skillStatus === status}
                          onChange={(e) => setSkillStatus(e.target.value)}
                          className="mr-3 text-blue-600 focus:ring-blue-500"
                        />
                        <span className="text-gray-900">{status}</span>
                      </label>
                    ))}
                  </div>
                </div>
              )}

              {/* Step 4: Date Observed */}
              {currentStep === 4 && (
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Date Observed</h3>
                  <input
                    type="date"
                    value={dateObserved}
                    onChange={(e) => setDateObserved(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
              )}

              {/* Step 5: Supports Used */}
              {currentStep === 5 && (
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Supports Used (Optional)</h3>
                  <div className="grid grid-cols-2 gap-2">
                    {supportsOptions.map((support) => (
                      <label key={support} className="flex items-center">
                        <input
                          type="checkbox"
                          checked={supportsUsed.includes(support)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              setSupportsUsed([...supportsUsed, support]);
                            } else {
                              setSupportsUsed(supportsUsed.filter(s => s !== support));
                            }
                          }}
                          className="mr-2 text-blue-600 focus:ring-blue-500"
                        />
                        <span className="text-gray-900 capitalize">{support}</span>
                      </label>
                    ))}
                  </div>
                </div>
              )}

              {/* Step 6: Context and Notes */}
              {currentStep === 6 && (
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Context and Notes</h3>
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">Context (Optional)</label>
                    <div className="grid grid-cols-2 gap-2">
                      {contextOptions.map((ctx) => (
                        <label key={ctx} className="flex items-center">
                          <input
                            type="checkbox"
                            checked={context.includes(ctx)}
                            onChange={(e) => {
                              if (e.target.checked) {
                                setContext([...context, ctx]);
                              } else {
                                setContext(context.filter(c => c !== ctx));
                              }
                            }}
                            className="mr-2 text-blue-600 focus:ring-blue-500"
                          />
                          <span className="text-gray-900 capitalize">{ctx}</span>
                        </label>
                      ))}
                    </div>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Notes (Optional, 200 chars)</label>
                    <textarea
                      value={notes}
                      onChange={(e) => setNotes(e.target.value.slice(0, 200))}
                      rows={3}
                      placeholder="Add any additional notes..."
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                    <div className="text-right text-sm text-gray-500 mt-1">
                      {notes.length}/200
                    </div>
                  </div>
                </div>
              )}
            </div>

            {/* Navigation */}
            <div className="flex items-center justify-between pt-6 border-t">
              <div>
                {currentStep > 1 && (
                  <button
                    onClick={handlePrevStep}
                    className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                  >
                    Back
                  </button>
                )}
              </div>
              <div className="flex space-x-3">
                <button
                  onClick={() => setShowAddSkillSheet(false)}
                  className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                  Cancel
                </button>
                {currentStep === 6 ? (
                  <>
                    <button
                      onClick={handleSave}
                      className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      Save
                    </button>
                    <button
                      onClick={handleSaveAndAddAnother}
                      className="px-4 py-2 text-sm font-medium text-blue-600 bg-blue-50 border border-blue-200 rounded-md hover:bg-blue-100 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      Save & Add Another
                    </button>
                  </>
                ) : (
                  <button
                    onClick={handleNextStep}
                    disabled={!selectedDomain || (currentStep === 2 && !selectedSkill)}
                    className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Next
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Placeholder Report Modal */}
      {showReportModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3 text-center">
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                Generate Milestone Report
              </h3>
              <p className="text-sm text-gray-500 mb-4">
                This feature will be implemented in Phase 5C.
              </p>
              <button
                onClick={() => setShowReportModal(false)}
                className="px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default GrowthDevelopmentScreen;
