sourcesContent:
import React, { useState } from 'react';

interface SymptomNutrientInsightsProps {
  onBack: () => void;
  symptomName?: string;
  severity?: number;
  date?: string;
}

interface SymptomEntry {
  id: string;
  symptom: string;
  severity: number;
  duration: string;
  triggers: string[];
  notes: string;
  timestamp: Date;
  domain: 'motor' | 'communication' | 'social' | 'sensory' | 'sleep' | 'gi' | 'seizures' | 'pain' | 'general' | 'custom';
  customSymptom?: string;
}

interface Pattern {
  symptom: string;
  frequency: string;
  commonTriggers: string[];
  timeOfDay: string;
  correlation: number;
}

interface SymptomTemplate {
  domain: string;
