    isOpen: boolean;
    title: string;
    message: string;
    confirmText: string;
    cancelText: string;
    variant?: 'default' | 'destructive';
    onConfirm: () => void;
  }>({
    isOpen: false,
    title: '',
    message: '',
    confirmText: '',
    cancelText: '',
    variant: 'default',
    onConfirm: () => {}
  });

  const greeting = `Good ${currentTime}, Jamie's Mom!`;

  // Toast functions
  const addToast = (toast: Omit<Toast, 'id'>) => {
    const newToast: Toast = {
      ...toast,
      id: Date.now().toString()
    };
    setToasts(prev => [...prev, newToast]);
  };

  const removeToast = (id: string) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  };

  // Confirm dialog functions
  const showConfirmDialog = (config: {
    title: string;
    message: string;
    confirmText: string;
    cancelText: string;
    variant?: 'default' | 'destructive';
    onConfirm: () => void;
  }) => {
    setConfirmDialog({
      ...config,
      isOpen: true
    });
  };

  const hideConfirmDialog = () => {
    setConfirmDialog(prev => ({ ...prev, isOpen: false }));
  };

  const handleQuickLog = () => {
    setShowQuickLogSelector(true);
  };

  const handleQuickLogSelect = (type: 'symptom' | 'nutrition' | 'medication') => {
    setShowQuickLogSelector(false);
    addToast({
      type: 'success',
      title: 'Log Created',
      message: `${type.charAt(0).toUpperCase() + type.slice(1)} log has been created successfully.`,
      duration: 3000
